import json
from xml.etree import ElementTree as ET
from collections import deque

def solve():
    root = ET.fromstring(map_xml_string)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    target_x = int(input_data['target_x'])
    target_y = int(input_data['target_y'])

    grid = [[{'passable': True, 'is_warp': False, 'type': 'UNKNOWN'} for _ in range(width)] for _ in range(height)]
    player_pos = None

    impassable_types = {'WALL', 'BUOY', 'ROCK', 'BOOKSHELF', 'TV', 'RADIO', 'TOWN_MAP', 'STATUE', 'COUNTER', 'HEADBUTT_TREE', 'CUT_TREE', 'MART_SHELF', 'VOID'}

    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib['type']
            is_warp = tile.attrib.get('is-warp') == 'true'
            has_player = tile.attrib.get('has-player') == 'true'
            has_object = tile.attrib.get('has-object') == 'true'

            grid[y][x]['type'] = t_type
            grid[y][x]['is_warp'] = is_warp

            if has_player:
                player_pos = (x, y)

            if t_type in impassable_types or has_object:
                grid[y][x]['passable'] = False

            if t_type == 'LEDGE_HOP_DOWN':
                grid[y][x]['passable'] = False

            if (is_warp or t_type == 'PIT') and (x != target_x or y != target_y):
                grid[y][x]['passable'] = False

    if not player_pos:
        print("[]")
        return

    queue = deque([(player_pos, [])])
    visited = {player_pos}

    while queue:
        (curr_x, curr_y), path = queue.popleft()
        if curr_x == target_x and curr_y == target_y:
            print(json.dumps(path))
            return

        for dx, dy, btn in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            nx, ny = curr_x + dx, curr_y + dy
            if 0 <= nx < width and 0 <= ny < height and (nx, ny) not in visited:
                if grid[ny][nx]['passable'] or (nx == target_x and ny == target_y):
                    visited.add((nx, ny))
                    queue.append(((nx, ny), path + [btn]))
    print("[]")

solve()
