import json
import xml.etree.ElementTree as ET

def solve(input_data):
    root = ET.fromstring(map_xml_string)
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])

    grid = {}
    for row in root.findall("Row"):
        y = int(row.attrib['id'])
        for tile in row.findall("Tile"):
            x = int(tile.attrib['id'])
            t_type = tile.attrib['type']
            seen = tile.attrib['seen'] == 'true'
            if not seen:
                t_type = "FLOOR"
            grid[(x, y)] = t_type

    queue = [(start_x, start_y, [])]
    visited = set()
    visited.add((start_x, start_y))

    while queue:
        cx, cy, path = queue.pop(0)
        if cx == end_x and cy == end_y:
            print(json.dumps(path))
            return

        for dx, dy, btn in [(0, 1, "Down"), (0, -1, "Up"), (1, 0, "Right"), (-1, 0, "Left")]:
            nx, ny = cx + dx, cy + dy
            if (nx, ny) in grid:
                current_type = grid[(cx, cy)]
                target_type = grid[(nx, ny)]

                # WALL collision
                if target_type == "WALL":
                    continue

                # FLOOR_UP_WALL collision (acts as wall on North edge)
                if current_type == "FLOOR_UP_WALL" and btn == "Up":
                    continue
                if target_type == "FLOOR_UP_WALL" and btn == "Down":
                    continue

                # LEDGE collisions
                if target_type == "LEDGE_HOP_DOWN" and btn != "Down":
                    continue
                if target_type == "LEDGE_HOP_RIGHT" and btn != "Right":
                    continue
                if target_type == "LEDGE_HOP_LEFT" and btn != "Left":
                    continue

                if (nx, ny) not in visited:
                    visited.add((nx, ny))
                    queue.append((nx, ny, path + [btn]))
    print("[]")

solve(input_data)
