import xml.etree.ElementTree as ET
from collections import deque
import json

def solve():
    try:
        root = ET.fromstring(map_xml_string)
        width = int(root.get('width'))
        height = int(root.get('height'))

        # Find player tile and its parent Row
        player_tile = None
        player_row = None
        for row in root.findall('Row'):
            for tile in row.findall('Tile'):
                if tile.find('Player') is not None:
                    player_tile = tile
                    player_row = row
                    break
            if player_tile: break

        if not player_tile:
            print(json.dumps({"error": "Player position not found in map XML."}))
            return

        start_x = int(player_tile.get('id'))
        start_y = int(player_row.get('id'))

        target_x = int(input_data['target_x'])
        target_y = int(input_data['target_y'])

        if start_x == target_x and start_y == target_y:
            print(json.dumps([]))
            return

        # Collision map
        grid = {}
        for row in root.findall('Row'):
            y = int(row.get('id'))
            for tile in row.findall('Tile'):
                x = int(tile.get('id'))
                t_type = tile.get('type')
                has_obj = tile.get('has-object') == 'true'
                is_wall = (t_type in ["WALL", "BUOY"] or has_obj)
                grid[(x, y)] = {"wall": is_wall, "type": t_type}

        queue = deque([(start_x, start_y, [])])
        visited = {(start_x, start_y)}

        while queue:
            x, y, path = queue.popleft()

            if x == target_x and y == target_y:
                print(json.dumps(path))
                return

            for dx, dy, move in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
                nx, ny = x + dx, y + dy

                if 0 <= nx < width and 0 <= ny < height and (nx, ny) not in visited:
                    tile_info = grid.get((nx, ny), {"wall": True, "type": "unseen"})

                    if tile_info["wall"] and (nx, ny) != (target_x, target_y):
                        continue

                    if tile_info["type"] == "FLOOR_UP_WALL" and move == "Up":
                        continue

                    visited.add((nx, ny))
                    queue.append((nx, ny, path + [move]))
                    if len(path) > 1000: break

        print(json.dumps({"error": f"No path found to ({target_x}, {target_y})"}))
    except Exception as e:
        print(json.dumps({"error": str(e)}))

solve()
