import json
import xml.etree.ElementTree as ET
from collections import deque

def solve():
    try:
        # Use input_data directly (injected by harness)
        start_x = int(input_data.get('start_x', 0))
        start_y = int(input_data.get('start_y', 0))
        target_x = int(input_data.get('target_x', 0))
        target_y = int(input_data.get('target_y', 0))
        allow_water = input_data.get('allow_water', False)
        handle_unseen = input_data.get('handle_unseen', True)

        root = ET.fromstring(map_xml_string)
        width = int(root.attrib['width'])
        height = int(root.attrib['height'])

        grid = {}
        if handle_unseen:
            for y in range(height):
                for x in range(width):
                    grid[(x, y)] = True

        for row in root.findall('Row'):
            y = int(row.attrib['id'])
            for tile in row.findall('Tile'):
                x = int(tile.attrib['id'])
                t_type = tile.attrib['type']
                has_obj = tile.attrib.get('has-object') == 'true'
                seen = tile.attrib['seen'] == 'true'

                if not seen and not handle_unseen:
                    grid[(x, y)] = False
                    continue

                if t_type in ['WALL', 'BUOY', 'HEADBUTT_TREE', 'CUT_TREE', 'FRUIT_TREE', 'COUNTER']:
                    grid[(x, y)] = False
                elif t_type == 'WATER':
                    grid[(x, y)] = 'WATER'
                elif t_type == 'FLOOR_UP_WALL':
                    grid[(x, y)] = 'UP_WALL'
                elif t_type == 'LEDGE_HOP_DOWN':
                    grid[(x, y)] = 'LEDGE'
                else:
                    grid[(x, y)] = True

                if has_obj:
                    grid[(x, y)] = False

        queue = deque([((start_x, start_y), [])])
        visited = {(start_x, start_y)}

        while queue:
            (cx, cy), path = queue.popleft()

            if (cx, cy) == (target_x, target_y):
                res = path + [{"x": cx, "y": cy}]
                print(json.dumps(res))
                return

            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nx, ny = cx + dx, cy + dy

                if (nx, ny) in grid:
                    val = grid[(nx, ny)]
                    if val is False: continue
                    if not allow_water and val == 'WATER': continue

                    if dy == 1 and val == 'UP_WALL': continue
                    if dy == -1 and grid.get((cx, cy)) == 'UP_WALL': continue
                    if dy == -1 and val == 'LEDGE': continue

                    if (nx, ny) not in visited:
                        visited.add((nx, ny))
                        queue.append(((nx, ny), path + [{"x": cx, "y": cy}]))

        print(json.dumps([]))
    except Exception as e:
        print(json.dumps({"error": str(e)}))

solve()
