import heapq
import xml.etree.ElementTree as ET
import json

def find_path(start_x, start_y, target_x, target_y, mode="walking", autopress_buttons=False):
    try:
        root = ET.fromstring(map_xml_string)
    except Exception as e:
        print(f"Error parsing map XML: {e}")
        return []

    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    # 0 = Walkable, 1 = Obstacle
    grid = [[1 for _ in range(height)] for _ in range(width)]

    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            tile_type = tile.attrib.get('type', 'WALL')
            has_object = tile.attrib.get('has-object', 'false') == 'true'

            # Check for markers with object_id (assumed to be NPCs/Obstacles)
            has_object_marker = False
            for marker in tile.findall('Marker'):
                if marker.attrib.get('object_id'):
                    has_object_marker = True
                    break

            # Base walkability
            is_walkable = False
            if tile_type in ['FLOOR', 'GRASS', 'TALL_GRASS', 'LONG_GRASS', 'SAND', 'PUDDLE', 'SAVANNA', 'SPINNER_UP', 'SPINNER_DOWN', 'SPINNER_LEFT', 'SPINNER_RIGHT']:
                 is_walkable = True
            elif tile_type in ['WATER', 'DEEP_WATER'] and mode == 'surfing':
                 is_walkable = True
            elif tile_type.startswith('LEDGE'):
                 # Treat ledges as walkable for general pathing to allow traversing DOWN them, 
                 # but this is a simplification. Ideally, we'd restrict direction.
                 # For now, allowing them prevents blocking valid paths.
                 is_walkable = True
            elif tile_type in ['DOOR', 'WARP_CARPET', 'STAIRS_UP', 'STAIRS_DOWN']:
                 is_walkable = True

            # Obstacle overrides
            if has_object or has_object_marker:
                is_walkable = False

            if is_walkable:
                grid[x][y] = 0

    # Ensure start and target are treated as walkable (in case we are standing on an object/marker or targeting one)
    if 0 <= start_x < width and 0 <= start_y < height:
        grid[start_x][start_y] = 0
    if 0 <= target_x < width and 0 <= target_y < height:
        grid[target_x][target_y] = 0

    # BFS
    queue = [(0, start_x, start_y, [])]
    visited = set([(start_x, start_y)])
    moves = [(0, -1, "Up"), (0, 1, "Down"), (-1, 0, "Left"), (1, 0, "Right")]

    final_path = []

    while queue:
        cost, cx, cy, path = heapq.heappop(queue)

        if cx == target_x and cy == target_y:
            final_path = path
            break

        for dx, dy, button in moves:
            nx, ny = cx + dx, cy + dy
            if 0 <= nx < width and 0 <= ny < height:
                if grid[nx][ny] == 0 and (nx, ny) not in visited:
                    visited.add((nx, ny))
                    heapq.heappush(queue, (cost + 1, nx, ny, path + [button]))

    if autopress_buttons:
        print(json.dumps(final_path))
    else:
        print(final_path)

find_path(start_x, start_y, target_x, target_y, mode, autopress_buttons)
