import xml.etree.ElementTree as ET
from collections import deque
import json

def find_path(xml_string, start, target):
    try:
        root = ET.fromstring(xml_string)
    except Exception:
        return None

    grid = {}
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib['type']
            has_obj = tile.attrib.get('has-object') == 'true'
            grid[(x, y)] = (t_type, has_obj)

    queue = deque([(start, [])])
    visited = {start}

    while queue:
        (x, y), path = queue.popleft()
        if (x, y) == target:
            return path

        for dx, dy, move in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            nx, ny = x + dx, y + dy
            if (nx, ny) in grid:
                ntype, nobj = grid[(nx, ny)]
                if ntype == 'WALL' or nobj or ntype == 'BUOY':
                    continue
                if ntype == 'DOOR' and (nx, ny) != target:
                    continue
                if dy == 1 and ntype == 'FLOOR_UP_WALL':
                    continue
                if dy == -1 and ntype == 'LEDGE_HOP_DOWN':
                    continue

                if (nx, ny) not in visited:
                    visited.add((nx, ny))
                    queue.append(((nx, ny), path + [move]))
    return None

