import xml.etree.ElementTree as ET
from collections import deque
import json
import sys

def find_path(map_xml, target_x, target_y):
    root = ET.fromstring(map_xml)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    player_tile = root.find(".//Tile[Player]")
    if player_tile is None:
        return []
    start_x = int(player_tile.attrib['id'])
    start_y = int(player_tile.find("..").attrib['id'])

    if start_x == target_x and start_y == target_y:
        return []

    queue = deque([(start_x, start_y, [])])
    visited = {(start_x, start_y)}

    while queue:
        cx, cy, path = queue.popleft()

        for dx, dy, move in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            nx, ny = cx + dx, cy + dy

            if not (0 <= nx < width and 0 <= ny < height):
                continue
            if (nx, ny) in visited:
                continue

            tile = root.find(f".//Row[@id='{ny}']/Tile[@id='{nx}']")
            if tile is None:
                continue

            ttype = tile.attrib.get('type', 'unseen')
            has_obj = tile.attrib.get('has-object') == 'true' or tile.find('Object') is not None
            is_warp = tile.attrib.get('is-warp') == 'true'

            if ttype in ['WALL', 'BUOY', 'unseen'] or has_obj:
                continue

            if move == 'Down' and ttype == 'FLOOR_UP_WALL':
                continue

            if move == 'Up' and ttype == 'LEDGE_HOP_DOWN':
                continue

            if is_warp and (nx != target_x or ny != target_y):
                continue

            new_path = path + [move]
            if nx == target_x and ny == target_y:
                return new_path

            visited.add((nx, ny))
            queue.append((nx, ny, new_path))

    return []

