import xml.etree.ElementTree as ET
import heapq
import json

def find_path(map_xml_string, target_x, target_y, party_data_str, ignorable_coords_str=None, hazard_coords_str=None):
    root = ET.fromstring(map_xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))

    grid = [['' for _ in range(width + 1)] for _ in range(height + 1)]
    elevations = [[0 for _ in range(width + 1)] for _ in range(height + 1)]
    objects = set()
    start_x, start_y, start_facing = -1, -1, ''
    pikachu_pos = None

    ignorable_coords = set()
    if ignorable_coords_str:
        try:
            coords_list = json.loads(ignorable_coords_str)
            if isinstance(coords_list, list):
                for c in coords_list:
                    ignorable_coords.add(tuple(c))
        except (json.JSONDecodeError, TypeError):
            pass

    hazard_coords = set()
    if hazard_coords_str:
        try:
            hazards = json.loads(hazard_coords_str)
            for h in hazards:
                hazard_coords.add(tuple(h))
        except (json.JSONDecodeError, TypeError):
            pass

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')
            grid[y][x] = tile_type

            player_elem = tile_elem.find('Player')
            if player_elem is not None:
                start_x, start_y = x, y
                start_facing = player_elem.get('facing', 'down')

            obj_elem = tile_elem.find('Object')
            if obj_elem is not None and obj_elem.get('id-name') == 'Pikachu':
                pikachu_pos = (x, y)
            elif (x, y) not in ignorable_coords:
                boulder_elem = tile_elem.find('Boulder')
                if (obj_elem is not None) or boulder_elem is not None:
                    objects.add((x, y))

            if tile_type in ['elevated_ground', 'cleared_boulder_barrier', 'boulder_switch']:
                elevations[y][x] = 1

    if start_x == -1:
        return "Player tile not found in map XML."

    can_surf = False
    try:
        party_data = json.loads(party_data_str)
        for pokemon in party_data:
            if any(move.get('name') == 'SURF' for move in pokemon.get('moves', [])):
                can_surf = True
                break
    except (json.JSONDecodeError, TypeError):
        pass

    traversable_walking = ['ground', 'grass', 'steps', 'cleared_boulder_barrier', 'hole', 'spinner_stop', 'open_gate', 'gate_offscreen', 'elevated_ground', 'ladder_up', 'ladder_down', 'boulder_switch']
    traversable_surfing = ['water']

    is_target_impassable = (grid[target_y][target_x] not in traversable_walking and grid[target_y][target_x] not in traversable_surfing and not grid[target_y][target_x].startswith('spinner')) or (target_x, target_y) in objects

    destination_tiles = []
    if is_target_impassable:
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            adj_x, adj_y = target_x + dx, target_y + dy
            if 1 <= adj_x <= width and 1 <= adj_y <= height:
                adj_tile_type = grid[adj_y][adj_x]
                is_walkable = (adj_tile_type in traversable_walking or adj_tile_type.startswith('spinner'))
                is_surfable = (adj_tile_type in traversable_surfing and can_surf)
                if (is_walkable or is_surfable) and (adj_x, adj_y) not in objects:
                    destination_tiles.append((adj_x, adj_y))
    else:
        destination_tiles.append((target_x, target_y))

    if not destination_tiles:
        return "No traversable tile adjacent to the impassable target."

    open_set = []
    initial_g = 0
    initial_state = (start_x, start_y, 0, start_facing)
    heapq.heappush(open_set, (0 + min(abs(start_x - d_x) + abs(start_y - d_y) for d_x, d_y in destination_tiles), initial_g, initial_state))

    came_from = {}
    g_score = {initial_state: initial_g}

    final_destination_state = None

    while open_set:
        _, current_g, current_state = heapq.heappop(open_set)
        x, y, mode, facing = current_state

        if (x, y) in destination_tiles:
            final_destination_state = current_state
            break

        for move_dir, (dx, dy) in {'down': (0, 1), 'up': (0, -1), 'right': (1, 0), 'left': (-1, 0)}.items():
            nx, ny = x + dx, y + dy

            if not (1 <= nx <= width and 1 <= ny <= height):
                continue

            neighbor_state_mode = mode
            neighbor_tile_type = grid[ny][nx]

            pikachu_cost = 0
            if (nx, ny) == pikachu_pos and move_dir != facing:
                 pikachu_cost = 1

            is_ledge_hop = False
            if grid[ny][nx] == 'ledge':
                if move_dir == 'down':
                    ny += 1
                    if not (1 <= ny <= height):
                        continue
                    neighbor_tile_type = grid[ny][nx]
                    is_ledge_hop = True
                else:
                    continue

            current_elevation = elevations[y][x]
            neighbor_elevation = elevations[y+dy][x+dx]

            if current_elevation != neighbor_elevation:
                start_tile_type = grid[y][x]
                end_tile_type = grid[y+dy][x+dx]

                can_go_up = neighbor_elevation > current_elevation and start_tile_type in ['steps', 'cleared_boulder_barrier']
                can_go_down = neighbor_elevation < current_elevation and (start_tile_type in ['steps', 'cleared_boulder_barrier'] or end_tile_type in ['steps', 'cleared_boulder_barrier'])

                if not (can_go_up or can_go_down):
                    continue

            current_tile_type = grid[y][x]
            if neighbor_tile_type == 'water' and mode == 0 and can_surf:
                neighbor_state_mode = 1
            elif current_tile_type == 'water' and neighbor_tile_type in traversable_walking and mode == 1:
                neighbor_state_mode = 0
            elif neighbor_tile_type == 'water' and not can_surf:
                continue

            is_walkable = neighbor_tile_type in traversable_walking or neighbor_tile_type.startswith('spinner')
            is_surfable = neighbor_tile_type in traversable_surfing

            is_traversable = (is_walkable and neighbor_state_mode == 0) or (is_surfable and neighbor_state_mode == 1)

            if not is_traversable or (nx, ny) in objects or (nx, ny) in hazard_coords:
                continue

            tentative_g_score = current_g + 1 + pikachu_cost
            neighbor_state = (nx, ny, neighbor_state_mode, move_dir)

            if tentative_g_score < g_score.get(neighbor_state, float('inf')):
                came_from[neighbor_state] = current_state
                g_score[neighbor_state] = tentative_g_score
                h_score = min(abs(nx - d_x) + abs(ny - d_y) for d_x, d_y in destination_tiles)
                f_score = tentative_g_score + h_score
                heapq.heappush(open_set, (f_score, tentative_g_score, neighbor_state))

    if final_destination_state:
        path = []
        current = final_destination_state
        while current in came_from:
            path.append(current)
            current = came_from[current]
        path.append(initial_state)

        path_coords = []
        prev_facing = start_facing
        for state in reversed(path):
            x_coord, y_coord, _, facing = state
            step = {'x': x_coord, 'y': y_coord}
            if pikachu_pos and (x_coord, y_coord) == pikachu_pos and facing != prev_facing:
                 step['note'] = 'Pikachu turn step'
            path_coords.append(step)
            prev_facing = facing
        return path_coords

    return None

try:
    target_x_val = int(input_data['target_x'])
    target_y_val = int(input_data['target_y'])
    party_data_val = input_data['party_data']
    ignorable_coords_val = input_data.get('ignorable_coords')
    hazard_coords_val = input_data.get('hazard_coords')

    path = find_path(map_xml_string, target_x_val, target_y_val, party_data_val, ignorable_coords_val, hazard_coords_val)

    if isinstance(path, list):
        print(json.dumps(path))
    elif path is None:
        print(json.dumps([{'error': 'path not found'}]))
    else:
        print(json.dumps([{'error': path}]))

except Exception as e:
    import traceback
    print(json.dumps([{'error': str(e), 'traceback': traceback.format_exc()}]))

