import xml.etree.ElementTree as ET
import heapq
import json

def solve_boulder_puzzle(map_xml_string, target_boulder_x, target_boulder_y, target_switch_x, target_switch_y):
    root = ET.fromstring(map_xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))

    grid = set()
    impassable = set()
    player_pos = None
    boulder_pos = (int(target_boulder_x), int(target_boulder_y))
    target_pos = (int(target_switch_x), int(target_switch_y))

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')
            grid.add((x, y))
            if tile_type == 'impassable' or tile_elem.find('Object') is not None:
                impassable.add((x, y))
            if tile_elem.get('has-player') == 'true':
                player_pos = (x, y)

    if not player_pos:
        return json.dumps([{'error': 'Player not found on map.'}])

    def heuristic(b_pos, t_pos):
        return abs(b_pos[0] - t_pos[0]) + abs(b_pos[1] - t_pos[1])

    def get_neighbors(p_pos, b_pos, current_impassable):
        neighbors = []
        for dx, dy in [(0,1), (0,-1), (1,0), (-1,0)]:
            np_pos = (p_pos[0] + dx, p_pos[1] + dy)
            if np_pos in grid and np_pos not in current_impassable:
                neighbors.append(np_pos)
        return neighbors

    def find_player_path(start, end, b_pos):
        temp_impassable = impassable.copy()
        temp_impassable.add(b_pos)
        q = [(0, [start])]
        visited = {start}
        while q:
            cost, path = heapq.heappop(q)
            node = path[-1]
            if node == end:
                return path
            for neighbor in get_neighbors(node, b_pos, temp_impassable):
                if neighbor not in visited:
                    visited.add(neighbor)
                    new_path = list(path)
                    new_path.append(neighbor)
                    heapq.heappush(q, (cost + 1, new_path))
        return None

    start_state = (player_pos, boulder_pos)
    open_set = [(heuristic(boulder_pos, target_pos), 0, [], start_state)] 
    visited_states = {start_state}

    while open_set:
        _, cost, path, current_state = heapq.heappop(open_set)
        p_pos, b_pos = current_state

        if b_pos == target_pos:
            return json.dumps(path)

        for push_dir_name, (dx, dy) in {'Right':(1,0), 'Left':(-1,0), 'Down':(0,1), 'Up':(0,-1)}.items():
            player_push_pos = (b_pos[0] - dx, b_pos[1] - dy)
            new_boulder_pos = (b_pos[0] + dx, b_pos[1] + dy)

            if new_boulder_pos not in grid or new_boulder_pos in impassable:
                continue

            player_path_to_push = find_player_path(p_pos, player_push_pos, b_pos)
            if player_path_to_push:
                new_player_pos = b_pos
                new_state = (new_player_pos, new_boulder_pos)
                if new_state not in visited_states:
                    visited_states.add(new_state)
                    new_cost = cost + len(player_path_to_push) -1 + 1
                    h = heuristic(new_boulder_pos, target_pos)
                    new_path_actions = path + [{'type': 'move', 'path': player_path_to_push[1:]}] + [{'type': 'push', 'direction': push_dir_name}]
                    heapq.heappush(open_set, (new_cost + h, new_cost, new_path_actions, new_state))

    return json.dumps([{'error': 'No solution found'}])

try:
    target_boulder_x_val = input_data['target_boulder_x']
    target_boulder_y_val = input_data['target_boulder_y']
    target_switch_x_val = input_data['target_switch_x']
    target_switch_y_val = input_data['target_switch_y']
    result = solve_boulder_puzzle(map_xml_string, target_boulder_x_val, target_boulder_y_val, target_switch_x_val, target_switch_y_val)
    print(result)
except Exception as e:
    import traceback
    print(json.dumps([{'error': str(e), 'traceback': traceback.format_exc()}]))
