    elif current == 2:
        if target == 1: 
            buttons.append("Up")
            added_nav = True
        elif target == 2: pass
        elif target == 3: 
            buttons.append("Down")
            added_nav = True
        elif target == 4: 
            buttons.extend(["Down", "Down"])
            added_nav = True
    elif current == 3:
        if target == 1: 
            buttons.extend(["Up", "Up"])
            added_nav = True
        elif target == 2: 
            buttons.append("Up")
            added_nav = True
        elif target == 3: pass
        elif target == 4: 
            buttons.append("Down")
            added_nav = True
    elif current == 4:
