import xml.etree.ElementTree as ET
from collections import deque
import json
import sys

# Arguments: start_x, start_y, target_x, target_y, output_format

def get_buttons(path):
    btns = []
    for i in range(1, len(path)):
        cx, cy = path[i-1]
        nx, ny = path[i]
        if nx > cx: btns.append("Right")
        elif nx < cx: btns.append("Left")
        elif ny > cy: btns.append("Down")
        elif ny < cy: btns.append("Up")
    return btns

try:
    # Ensure map_xml_string exists
    if 'map_xml_string' not in globals():
        raise ValueError("map_xml_string not found in globals")

    map_data = ET.fromstring(map_xml_string)
    width = int(map_data.attrib['width'])
    height = int(map_data.attrib['height'])

    grid = [[0 for _ in range(height)] for _ in range(width)]

    for row in map_data.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib.get('type', 'WALL')
            has_obj = tile.attrib.get('has-object', 'false') == 'true'
            has_player = tile.attrib.get('has-player', 'false') == 'true'

            # Treat WALL, COUNTER, and Objects (NPCs/Items) as blocked.
            if t_type in ['WALL', 'COUNTER', 'boulder', 'ROCK', 'TREE'] or (has_obj and not has_player):
                grid[x][y] = 1
            else:
                grid[x][y] = 0

    if 0 <= start_x < width and 0 <= start_y < height:
        grid[start_x][start_y] = 0

    queue = deque([(start_x, start_y)])
    came_from = {}
    came_from[(start_x, start_y)] = None
    found = False

    while queue:
        cx, cy = queue.popleft()
        if (cx, cy) == (target_x, target_y):
            found = True
            break

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = cx + dx, cy + dy
            if 0 <= nx < width and 0 <= ny < height:
                if grid[nx][ny] == 0 and (nx, ny) not in came_from:
                    came_from[(nx, ny)] = (cx, cy)
                    queue.append((nx, ny))

    if found:
        path = []
        curr = (target_x, target_y)
        while curr:
            path.append(curr)
            curr = came_from[curr]
        path.reverse()

        fmt = locals().get('output_format', 'coords')

        if fmt == 'buttons':
            print(json.dumps(get_buttons(path)))
        else:
            out_path = [{"x": p[0], "y": p[1]} for p in path]
            print(json.dumps(out_path))
    else:
        # Return empty list if no path found, but valid JSON
        print(json.dumps([]))

except Exception as e:
    # Print the error message to stdout so it appears in tool output
    print(f"Error: {str(e)}")
