import json
import xml.etree.ElementTree as ET
from collections import deque

def solve():
    # Use the global map_xml_string
    root = ET.fromstring(map_xml_string)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    # Find player position
    player_x, player_y = None, None
    grid = {}
    objects = set()
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            tile_type = tile.attrib['type']
            grid[(x, y)] = tile_type
            if tile.find('Player') is not None:
                player_x, player_y = x, y
            if tile.get('has-object') == 'true':
                # Only treat objects as walls if they aren't the player
                if tile.find('Player') is None:
                    objects.add((x, y))

    if player_x is None:
        print("Error: Player position not found in map XML.")
        return

    target_x = int(input_data['target_x'])
    target_y = int(input_data['target_y'])

    # Collision types that are impassable
    impassable_types = ['WALL', 'unseen', 'HEADBUTT_TREE', 'WATER']

    # If target is impassable, find adjacent traversable tile
    target_type = grid.get((target_x, target_y), 'unseen')
    is_target_impassable = target_type in impassable_types or (target_x, target_y) in objects

    targets = [(target_x, target_y)]
    if is_target_impassable:
        targets = []
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = target_x + dx, target_y + dy
            if 0 <= nx < width and 0 <= ny < height:
                if grid.get((nx, ny)) not in impassable_types and (nx, ny) not in objects:
                    targets.append((nx, ny))

        if not targets:
            print(f"Error: Target ({target_x}, {target_y}) is impassable and no adjacent traversable tiles found.")
            return

    # BFS
    queue = deque([(player_x, player_y, [])])
    visited = {(player_x, player_y)}

    while queue:
        x, y, path = queue.popleft()
        if (x, y) in targets:
            full_path = path + [{'x': x, 'y': y}]
            print(json.dumps(full_path))
            return

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < width and 0 <= ny < height and (nx, ny) not in visited:
                if grid.get((nx, ny)) not in impassable_types and (nx, ny) not in objects:
                    visited.add((nx, ny))
                    queue.append((nx, ny, path + [{'x': x, 'y': y}]))

    print(f"Error: No path found to ({target_x}, {target_y}) or its adjacent tiles.")

solve()
