import xml.etree.ElementTree as ET
import heapq
import json

class AStar:
    def __init__(self, map_xml_string):
        self.root = ET.fromstring(map_xml_string)
        self.width = int(self.root.get('width'))
        self.height = int(self.root.get('height'))

        self.tile_map = {}
        for row_node in self.root:
            if row_node.tag != 'Row': continue
            y = int(row_node.get('id'))
            for tile_node in row_node:
                if tile_node.tag != 'Tile': continue
                x = int(tile_node.get('id'))
                self.tile_map[(x, y)] = tile_node

        self.player_pos = self._get_player_pos()
        self.elevation_map = {
            'ground': 0, 'grass': 0, 'steps': 1, 'boulder_switch': 1,
            'cleared_boulder_barrier': 1, 'ladder_up': 1, 'ladder_down': 1, 'elevated_ground': 2
        }

    def _get_tile(self, x, y):
        return self.tile_map.get((x, y))

    def _get_player_pos(self):
        for (x, y), tile_node in self.tile_map.items():
            if tile_node.find('Player') is not None:
                return x, y
        return None

    def _get_impassable_objects(self, ignorable_coords_str):
        objects = set()
        ignorable = []
        if ignorable_coords_str and ignorable_coords_str.lower() != 'null':
            try: 
                ignorable_data = json.loads(ignorable_coords_str)
                if isinstance(ignorable_data, list):
                    ignorable = [(d['x'], d['y']) for d in ignorable_data if isinstance(d, dict) and 'x' in d and 'y' in d]
            except (json.JSONDecodeError, TypeError): pass

        for (x, y), t in self.tile_map.items():
            if (x, y) in ignorable: continue
            if t.get('type') in {'impassable', 'boulder_barrier'}: 
                objects.add((x, y))
            elif t.find("Object[@id!='15']") is not None: 
                objects.add((x, y))
            elif t.find('Boulder') is not None: 
                objects.add((x, y))
        return objects

    def is_traversable(self, current_pos, neighbor_pos, traversable_tiles, impassable_objects, end_node):
        n_x, n_y = neighbor_pos

        if (n_x, n_y) in impassable_objects and (n_x, n_y) != end_node:
            return False

        neighbor_tile = self._get_tile(n_x, n_y)
        if not neighbor_tile:
            return False

        neighbor_type = neighbor_tile.get('type')
        if neighbor_type == 'impassable' and (n_x, n_y) != end_node:
            return False

        if neighbor_pos != end_node and neighbor_type not in traversable_tiles and neighbor_type != 'ledge':
            return False

        current_tile = self._get_tile(current_pos[0], current_pos[1])
        current_type = current_tile.get('type')

        if neighbor_type == 'ledge':
            return n_y > current_pos[1]

        current_elev = self.elevation_map.get(current_type, -1)
        neighbor_elev = self.elevation_map.get(neighbor_type, -1)

        if abs(current_elev - neighbor_elev) > 1:
            return False
        if neighbor_elev > current_elev and current_type not in {'steps', 'ladder_up', 'ladder_down', 'cleared_boulder_barrier'}:
            return False
        if current_elev > neighbor_elev and neighbor_type not in {'steps', 'ladder_up', 'ladder_down', 'cleared_boulder_barrier'}:
             return False

        return True

    def find_path(self, target_x, target_y, ignorable_coords_str=None, is_surfing=False):
        if not self.player_pos: return print(json.dumps({"error": "Player position not found."}))

        traversable_tiles = {'ground', 'grass', 'elevated_ground', 'steps', 'cleared_boulder_barrier', 'boulder_switch', 'ladder_up', 'ladder_down'}
        if is_surfing: traversable_tiles.add('water')
        impassable = self._get_impassable_objects(ignorable_coords_str)
        start_node, end_node = self.player_pos, (int(target_x), int(target_y))

        if end_node in impassable:
            potential_goals = []
            for neighbor in [(end_node[0]-1, end_node[1]), (end_node[0]+1, end_node[1]), (end_node[0], end_node[1]-1), (end_node[0], end_node[1]+1)]:
                if (1 <= neighbor[0] <= self.width and 1 <= neighbor[1] <= self.height) and (neighbor not in impassable):
                    neighbor_tile = self._get_tile(neighbor[0], neighbor[1])
                    if neighbor_tile is not None and neighbor_tile.get('type') in traversable_tiles:
                         potential_goals.append(neighbor)
            if not potential_goals: 
                return print(json.dumps({"error": "No traversable tile adjacent to the impassable destination."}))
            potential_goals.sort(key=lambda p: abs(p[0] - start_node[0]) + abs(p[1] - start_node[1]))
            end_node = potential_goals[0]

        open_list, counter, closed_set, came_from, g_score = [], 0, set(), {start_node: None}, {start_node: 0}
        heapq.heappush(open_list, (0, counter, start_node))

        while open_list:
            _, _, current = heapq.heappop(open_list)
            if current in closed_set: continue
            closed_set.add(current)

            if current == end_node:
                path = []
                _curr = end_node
                while _curr: path.append(_curr); _curr = came_from.get(_curr)
                path.reverse()
                return print(json.dumps([{'x': p[0], 'y': p[1]} for p in path]))

            for neighbor in [(current[0]-1, current[1]), (current[0]+1, current[1]), (current[0], current[1]-1), (current[0], current[1]+1)]:
                if not (1 <= neighbor[0] <= self.width and 1 <= neighbor[1] <= self.height): continue
                if self.is_traversable(current, neighbor, traversable_tiles, impassable, end_node):
                    tentative_g = g_score.get(current, float('inf')) + 1
                    if tentative_g < g_score.get(neighbor, float('inf')):
                        came_from[neighbor], g_score[neighbor] = current, tentative_g
                        h = abs(neighbor[0] - end_node[0]) + abs(neighbor[1] - end_node[1])
                        counter += 1
                        heapq.heappush(open_list, (tentative_g + h, counter, neighbor))

        return print(json.dumps({"error": "No path found."}))

try:
    pathfinder = AStar(map_xml_string)
    pathfinder.find_path(input_data['target_x'], input_data['target_y'], input_data.get('ignorable_coords'), input_data.get('is_surfing') == 'true')
except Exception as e:
    print(json.dumps({"error": f"An unexpected error occurred: {str(e)}"}))

