import xml.etree.ElementTree as ET
import json

puzzle_state = {
    "player_pos": {},
    "boulders": [],
    "switches": [],
    "barriers": [],
    "impassable_tiles": []
}

root = ET.fromstring(map_xml_string)
impassable_types = ['impassable', 'boulder_barrier', 'closed_gate']

for row in root.findall('Row'):
    y = int(row.get('id'))
    for tile in row.findall('Tile'):
        x = int(tile.get('id'))
        coord = {'x': x, 'y': y}

        if tile.find('Player') is not None:
            puzzle_state["player_pos"] = coord

        if tile.find('Boulder') is not None:
            puzzle_state["boulders"].append(coord)
        if tile.get('type') == 'boulder_switch':
            puzzle_state["switches"].append(coord)
        if tile.get('type') == 'boulder_barrier':
            puzzle_state["barriers"].append(coord)

        is_impassable = False
        if tile.get('type') in impassable_types:
            is_impassable = True

        for obj in tile.findall('Object'):
            if obj.get('id-name') != 'Pikachu':
                is_impassable = True
                break

        if is_impassable:
            puzzle_state["impassable_tiles"].append(coord)

print(json.dumps(puzzle_state))

