import json

actions_str = input_data.get('actions', '[]')
try:
    actions = json.loads(actions_str)
except json.JSONDecodeError:
    print(json.dumps({"error": "Invalid JSON format for actions."}))
    return

button_presses = []

for action in actions:
    direction = action.get('direction')
    try:
        count = int(action.get('count', 0))
    except (ValueError, TypeError):
        count = 0

    if direction and count > 0:
        if direction in ['LEFT', 'RIGHT']:
            for _ in range(count):
                button_presses.append(direction)
        elif direction in ['UP', 'DOWN']:
            reposition_move = 'DOWN' if direction == 'UP' else 'UP'
            for i in range(count):
                button_presses.append(direction)
                if i < count - 1:
                    button_presses.append(reposition_move)

print(json.dumps({"button_presses": button_presses}))
