import xml.etree.ElementTree as ET
import heapq
import json

def find_shortest_path_a_star(start, end, grid, map_width, map_height):
    open_set = []
    heapq.heappush(open_set, (0, start))
    came_from = {}
    g_score = { (c,r): float('inf') for r in range(map_height + 2) for c in range(map_width + 2) }
    g_score[start] = 0
    f_score = { (c,r): float('inf') for r in range(map_height + 2) for c in range(map_width + 2) }
    f_score[start] = abs(start[0] - end[0]) + abs(start[1] - end[1])

    while open_set:
        _, current = heapq.heappop(open_set)
        if current == end:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            return len(path)

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)
            if not (1 <= neighbor[0] <= map_width and 1 <= neighbor[1] <= map_height):
                continue
            if grid[neighbor[1]][neighbor[0]] == 1:
                continue
            tentative_g_score = g_score[current] + 1
            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = tentative_g_score + (abs(neighbor[0] - end[0]) + abs(neighbor[1] - end[1]))
                heapq.heappush(open_set, (f_score[neighbor], neighbor))
    return float('inf')

root = ET.fromstring(map_xml_string)
map_width = int(root.get('width'))
map_height = int(root.get('height'))
player_x = int(input_data['player_x'])
player_y = int(input_data['player_y'])
unsolved_puzzles_str = input_data['unsolved_puzzles']
unsolved_puzzles = json.loads(unsolved_puzzles_str)

grid = [[0 for _ in range(map_width + 1)] for _ in range(map_height + 1)]
for row_elem in root.findall('Row'):
    y = int(row_elem.get('id'))
    for tile_elem in row_elem.findall('Tile'):
        x = int(tile_elem.get('id'))
        tile_type = tile_elem.get('type')
        is_pikachu = tile_elem.find("./Object[@id-name='Pikachu']") is not None
        has_object = tile_elem.find('Object') is not None
        is_impassable = False
        impassable_types = ['impassable', 'boulder_barrier', 'closed_gate', 'unknown', 'gate_offscreen']
        if tile_type in impassable_types or (has_object and not is_pikachu):
            is_impassable = True
        if is_impassable:
            grid[y][x] = 1

shortest_path = float('inf')
best_target = None

for puzzle in unsolved_puzzles:
    target_x = puzzle['x']
    target_y = puzzle['y']

    interaction_x, interaction_y = target_x, target_y + 1

    if not (1 <= interaction_x <= map_width and 1 <= interaction_y <= map_height) or grid[interaction_y][interaction_x] == 1:
        continue

    path_length = find_shortest_path_a_star((player_x, player_y), (interaction_x, interaction_y), grid, map_width, map_height)
    if path_length < shortest_path:
        shortest_path = path_length
        best_target = {'x': target_x, 'y': target_y}

if best_target:
    print(json.dumps(best_target))
else:
    print(json.dumps({'error': 'No reachable unsolved puzzles found.'}))

