import xml.etree.ElementTree as ET
import heapq

# Corrected A* implementation for spinner maze

def parse_map_xml(xml_string):
    root = ET.fromstring(xml_string)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])
    grid = [[0 for _ in range(width)] for _ in range(height)]
    player_pos = None
    spinners = {}

    for row_elem in root.findall('Row'):
        y = int(row_elem.attrib['id']) - 1
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.attrib['id']) - 1
            tile_type = tile_elem.attrib['type']

            traversable = tile_type not in ['impassable', 'unknown']
            if tile_elem.find('Object') is not None:
                obj = tile_elem.find('Object')
                # Pikachu is the only traversable object
                if 'id-name' in obj.attrib and obj.attrib['id-name'] != 'Pikachu':
                    traversable = False

            grid[y][x] = 1 if traversable else 0

            if tile_elem.find('Player') is not None:
                player_pos = (x, y)

            spinner_elem = tile_elem.find('Spinner')
            if spinner_elem is not None:
                direction = spinner_elem.attrib.get('direction')
                end_coord_str = spinner_elem.attrib.get('end-coordinate')
                end_coord = tuple(map(int, end_coord_str.split(','))) if end_coord_str else None
                spinners[(x, y)] = {'direction': direction, 'end': end_coord}

    return grid, player_pos, spinners, width, height

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def get_neighbors(pos, grid, spinners, width, height):
    x, y = pos
    neighbors = []

    if pos in spinners:
        spinner = spinners[pos]
        direction = spinner['direction']
        end_pos = spinner['end']

        if end_pos:
            neighbors.append(end_pos)
        else:
            # Handle unknown end-point by moving one step
            if direction == 'up' and y > 0: neighbors.append((x, y - 1))
            elif direction == 'down' and y < height - 1: neighbors.append((x, y + 1))
            elif direction == 'left' and x > 0: neighbors.append((x - 1, y))
            elif direction == 'right' and x < width - 1: neighbors.append((x + 1, y))
    else:
        # Standard neighbors for non-spinner tiles
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < width and 0 <= ny < height and grid[ny][nx] == 1:
                neighbors.append((nx, ny))

    return neighbors

try:
    target_x = int(input_data['target_x'])
    target_y = int(input_data['target_y'])
    grid, start, spinners, width, height = parse_map_xml(map_xml_string)
    target = (target_x - 1, target_y - 1)

    if not start:
        print("Player position not found.")
    elif not (0 <= target[0] < width and 0 <= target[1] < height and grid[target[1]][target[0]] == 1):
        print("Target is not traversable or out of bounds.")
    else:
        open_set = [(0, start)]
        came_from = {}
        g_score = {start: 0}

        while open_set:
            _, current = heapq.heappop(open_set)

            if current == target:
                path = []
                while current in came_from:
                    path.append(current)
                    current = came_from[current]
                path.append(start)
                path.reverse()
                # Convert to 1-based coordinates for output
                path_1_based = [(x + 1, y + 1) for x, y in path]
                print(path_1_based)
                break

            for neighbor in get_neighbors(current, grid, spinners, width, height):
                tentative_g_score = g_score[current] + 1
                if neighbor not in g_score or tentative_g_score < g_score[neighbor]:
                    came_from[neighbor] = current
                    g_score[neighbor] = tentative_g_score
                    f_score = tentative_g_score + heuristic(neighbor, target)
                    heapq.heappush(open_set, (f_score, neighbor))
        else:
            print("No path found.")

except Exception as e:
    print(f"An error occurred: {e}")
