import xml.etree.ElementTree as ET
import json
import heapq

def find_path_a_star(grid, start, end, all_impassable, debug_info):
    impassable_base = {
        'WALL', 'VOID', 'WATER', 'CUT_TREE', 'COUNTER', 'BUOY', 'HEADBUTT_TREE', 'unseen',
        'MART_SHELF', 'BOOKSHELF', 'TV', 'RADIO', 'PC', 'TOWN_MAP', 'WINDOW', 'PAINTING', 'STATUE', 'PILLAR',
        'BED', 'TABLE', 'CHAIR'
    }

    debug_info['impassable_base_types'] = list(impassable_base)
    debug_info['impassable_coordinates_from_input'] = [list(coord) for coord in all_impassable]
    debug_info['rejection_log'] = []
    debug_info['ledge_debug'] = [] # Added for debugging

    if start not in grid:
        debug_info['error'] = f'Start position {start} not in grid.'
        return None, None, None
    if end not in grid:
        debug_info['error'] = f'End position {end} not in grid.'
        return None, None, None

    if grid.get(start, {}).get('type') in impassable_base or start in all_impassable:
        debug_info['error'] = f'Start position {start} is on an impassable tile type or object.'
        return None, None, None

    def heuristic(a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    open_set = []
    heapq.heappush(open_set, (0, start))
    came_from = {}
    g_score = {pos: float('inf') for pos in grid}
    g_score[start] = 0
    f_score = {pos: float('inf') for pos in grid}
    f_score[start] = heuristic(start, end)

    open_set_hash = {start}

    while open_set:
        _, current_pos = heapq.heappop(open_set)
        open_set_hash.remove(current_pos)

        if current_pos == end:
            path = []
            path_tile_types = []
            temp_pos = current_pos
            while temp_pos in came_from:
                path.append(list(temp_pos))
                path_tile_types.append(grid.get(temp_pos, {}).get('type'))
                temp_pos = came_from[temp_pos]
            path.append(list(start))
            path_tile_types.append(grid.get(start, {}).get('type'))
            return path[::-1], path_tile_types[::-1], None

        current_type = grid.get(current_pos, {}).get('type')

        if 'LEDGE' in current_type:
            debug_info['ledge_debug'].append(f'At {current_pos}, found ledge type: {current_type}')

        for move_x, move_y, move_name in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            next_pos = (current_pos[0] + move_x, current_pos[1] + move_y)

            rejection_reason = ""

            if next_pos not in grid:
                rejection_reason = 'Out of bounds'
            else:
                next_type = grid.get(next_pos, {}).get('type')

                if next_type in impassable_base:
                    rejection_reason = f'Impassable base type: {next_type}'
                elif next_pos in all_impassable and next_pos != end:
                    rejection_reason = 'Impassable coordinate'
                elif current_type == 'LEDGE_HOP_DOWN' and move_name != 'Down':
                    rejection_reason = f'Cannot move {move_name} from LEDGE_HOP_DOWN'
                elif current_type == 'LEDGE_HOP_LEFT' and move_name != 'Left':
                    rejection_reason = f'Cannot move {move_name} from LEDGE_HOP_LEFT'
                elif current_type == 'LEDGE_HOP_RIGHT' and move_name != 'Right':
                    rejection_reason = f'Cannot move {move_name} from LEDGE_HOP_RIGHT'
                elif current_type == 'FLOOR_UP_WALL' and move_name in ['Up', 'Down']:
                    rejection_reason = f'Cannot move {move_name} from FLOOR_UP_WALL'
                elif next_type == 'FLOOR_UP_WALL' and move_name != 'Up':
                    rejection_reason = 'Cannot enter FLOOR_UP_WALL except by moving Up'
                elif next_type == 'LEDGE_HOP_DOWN' and move_name == 'Up':
                    rejection_reason = 'Cannot move Up onto a LEDGE_HOP_DOWN'
                elif next_type == 'LEDGE_HOP_LEFT' and move_name == 'Right':
                    rejection_reason = 'Cannot move Right onto a LEDGE_HOP_LEFT'
                elif next_type == 'LEDGE_HOP_RIGHT' and move_name == 'Left':
                    rejection_reason = 'Cannot move Left onto a LEDGE_HOP_RIGHT'

            if rejection_reason:
                debug_info['rejection_log'].append({'from': list(current_pos), 'to': list(next_pos), 'reason': rejection_reason})
                continue

            tentative_g_score = g_score[current_pos] + 1
            if tentative_g_score < g_score.get(next_pos, float('inf')):
                came_from[next_pos] = current_pos
                g_score[next_pos] = tentative_g_score
                f_score[next_pos] = tentative_g_score + heuristic(next_pos, end)
                if next_pos not in open_set_hash:
                    heapq.heappush(open_set, (f_score[next_pos], next_pos))
                    open_set_hash.add(next_pos)

    blocked_at = None
    if 'rejection_log' in debug_info and debug_info['rejection_log']:
        last_rejection = debug_info['rejection_log'][-1]
        if last_rejection['to'] == list(end):
             blocked_at = last_rejection['to']

    debug_info['error'] = 'No path found to destination.'
    return None, None, blocked_at

try:
