    loaded_on_screen = json.loads(on_screen_impassable_str) if on_screen_impassable_str else []
    on_screen_impassable = {tuple(map(int, coord)) for coord in loaded_on_screen} if loaded_on_screen else set()

    extra_impassable_str = input_data.get('extra_impassable_coordinates')
