            'extra_impassable': [list(c) for c in extra_impassable]
        }
    }

    root = ET.fromstring(map_xml_string)
    grid = {}

    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            grid[(x, y)] = {'type': tile.get('type')}

    all_impassable = on_screen_impassable.union(extra_impassable)

