import xml.etree.ElementTree as ET
from collections import deque
import json

def main(input_data):
    try:
        map_xml = globals().get('map_xml_string')
        if not map_xml:
            print(json.dumps({}))
            return

        start_x = int(input_data['start_x'])
        start_y = int(input_data['start_y'])
        has_surf = input_data.get('has_surf', True)
        autopress = input_data.get('autopress_buttons', False)
        start_pos = (start_x, start_y)

        root = ET.fromstring(map_xml)
        grid = {}
        for row in root.findall('Row'):
            y = int(row.attrib['id'])
            for tile in row.findall('Tile'):
                x = int(tile.attrib['id'])
                t_type = tile.attrib['type']
                is_seen = tile.attrib['seen'] == 'true'
                has_obj = any(obj.tag == 'Object' for obj in tile)
                grid[(x, y)] = {'type': t_type, 'seen': is_seen, 'blocked': has_obj}

        traversable = {'FLOOR', 'LADDER', 'TALL_GRASS', 'CAVE', 'WARP_CARPET_DOWN', 'WARP_CARPET_UP', 
                      'WARP_CARPET_LEFT', 'WARP_CARPET_RIGHT', 'POKECENTER', 'GRASS', 'WARP', 'POKEMART'}
        if has_surf: traversable.add('WATER')
        ledges = {'LEDGE_HOP_DOWN'}

        queue = deque([(start_pos, [])])
        visited = {start_pos}

        while queue:
            (x, y), path = queue.popleft()

            for dx, dy, btn in [(0, 1, 'Down'), (0, -1, 'Up'), (1, 0, 'Right'), (-1, 0, 'Left')]:
                nx, ny = x + dx, y + dy
                if (nx, ny) in grid:
                    target = grid[(nx, ny)]

                    if not target['seen']:
                        if autopress:
                            print(json.dumps(path + [btn]))
                        else:
                            print(json.dumps({"found": True, "x": nx, "y": ny, "buttons": path + [btn]}))
                        return

                    if target['blocked']: continue

                    if target['type'] in traversable:
                        if (nx, ny) not in visited:
                            visited.add((nx, ny))
                            queue.append(((nx, ny), path + [btn]))
                    elif target['type'] in ledges and dy == 1:
                        jx, jy = nx, ny + 1
                        if (jx, jy) in grid:
                            jt = grid[(jx, jy)]
                            if not jt['seen']:
                                if autopress:
                                    print(json.dumps(path + [btn, btn]))
                                else:
                                    print(json.dumps({"found": True, "x": jx, "y": jy, "buttons": path + [btn, btn]}))
                                return
                            if not jt['blocked'] and jt['type'] in traversable:
                                if (jx, jy) not in visited:
                                    visited.add((jx, jy))
                                    queue.append(((jx, jy), path + [btn, btn]))

        print(json.dumps({"found": False}))
    except Exception as e:
        print(json.dumps({"error": str(e)}))

main(input_data)
