import heapq
import json
import xml.etree.ElementTree as ET

class AStar:
    def __init__(self, map_xml_string):
        self.root = ET.fromstring(map_xml_string)
        self.width = int(self.root.get('width'))
        self.height = int(self.root.get('height'))
        self.grid = self._create_grid()

    def _create_grid(self):
        grid = [[0 for _ in range(self.width)] for _ in range(self.height)]
        for row_elem in self.root.findall('Row'):
            y = int(row_elem.get('id')) - 1
            for tile_elem in row_elem.findall('Tile'):
                x = int(tile_elem.get('id')) - 1
                tile_type = tile_elem.get('type')
                has_object = tile_elem.get('has-object') == 'true'
                is_warp = tile_elem.get('is-warp') == 'true'
                has_player = tile_elem.find('Player') is not None
                obj = tile_elem.find('Object')
                is_pikachu = obj is not None and obj.get('id-name') == 'PIKACHU'

                cost = 1 # Default cost
                if tile_type in ['impassable', 'unknown', 'closed_gate', 'boulder_barrier']:
                    cost = float('inf')
                elif has_object and not is_pikachu:
                    cost = float('inf')
                elif tile_type == 'ledge':
                    # Treat ledges as walls from below/sides for pathing purposes
                    # The actual traversal logic is handled in get_neighbors
                    pass
                elif is_warp:
                    # Make warps very expensive to avoid pathing through them unless necessary
                    cost = 1000

                grid[y][x] = cost
        return grid

    def heuristic(self, a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    def get_neighbors(self, node):
        x, y = node
        neighbors = []
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < self.width and 0 <= ny < self.height:
                # Ledge logic: can only jump down
                current_tile_elem = self.root.find(f".//Row[@id='{y + 1}']/Tile[@id='{x + 1}']")
                neighbor_tile_elem = self.root.find(f".//Row[@id='{ny + 1}']/Tile[@id='{nx + 1}']")
                if neighbor_tile_elem is not None:
                    neighbor_tile_type = neighbor_tile_elem.get('type')
                    if neighbor_tile_type == 'ledge' and dy == 1: # Moving down onto a ledge
                        # The actual move is to y+2, so the path should reflect that
                        nnx, nny = nx, ny + 1
                        if 0 <= nnx < self.width and 0 <= nny < self.height:
                            neighbors.append(((nnx, nny), 1)) # Cost of 1 for the jump
                        continue
                    elif neighbor_tile_type == 'ledge' and dy != 1:
                        continue # Can't move onto a ledge from side or below

                neighbors.append(((nx, ny), 1))

        return neighbors

    def find_path(self, start, end):
        # Make end tile walkable for pathfinding
        end_y, end_x = end[1]-1, end[0]-1
        original_cost = self.grid[end_y][end_x]
        self.grid[end_y][end_x] = 1
        if self.root.find(f".//Row[@id='{end[1]}']/Tile[@id='{end[0]}']").get('is-warp') == 'true':
             self.grid[end_y][end_x] = 1 # Allow pathing TO a warp

        start_node = (start[0] - 1, start[1] - 1)
        end_node = (end[0] - 1, end[1] - 1)

        open_set = [(0, start_node)]
        came_from = {}
        g_score = { (c, r): float('inf') for r in range(self.height) for c in range(self.width) }
        g_score[start_node] = 0
        f_score = { (c, r): float('inf') for r in range(self.height) for c in range(self.width) }
        f_score[start_node] = self.heuristic(start_node, end_node)

        visited_nodes_for_debug = set()

        while open_set:
            _, current = heapq.heappop(open_set)
            visited_nodes_for_debug.add(current)

            if current == end_node:
                self.grid[end_y][end_x] = original_cost # Restore cost
                return self.reconstruct_path(came_from, current)

            for neighbor, move_cost in self.get_neighbors(current):
                if self.grid[neighbor[1]][neighbor[0]] == float('inf'):
                    continue

                tentative_g_score = g_score[current] + move_cost

                if tentative_g_score < g_score[neighbor]:
                    came_from[neighbor] = current
                    g_score[neighbor] = tentative_g_score
                    f_score[neighbor] = tentative_g_score + self.heuristic(neighbor, end_node)
                    if neighbor not in [i[1] for i in open_set]:
                        heapq.heappush(open_set, (f_score[neighbor], neighbor))

        # Path not found, provide debug info
        self.grid[end_y][end_x] = original_cost # Restore cost
        debug_info = {
            "error": "No path found",
            "start": start,
            "end": end,
            "nodes_visited": len(visited_nodes_for_debug),
            "start_cost": self.grid[start_node[1]][start_node[0]],
            "end_cost": original_cost
        }
        return [debug_info]

    def reconstruct_path(self, came_from, current):
        path = []
        while current in came_from:
            path.append({'x': current[0] + 1, 'y': current[1] + 1})
            current = came_from[current]
        path.append({'x': current[0] + 1, 'y': current[1] + 1})
        return path[::-1]

def convert_path_to_directions(path):
    if not path or 'error' in path[0]:
        return path # Return error info
    directions = []
    for i in range(len(path) - 1):
        current = path[i]
        nxt = path[i+1]
        dx = nxt['x'] - current['x']
        dy = nxt['y'] - current['y']
        if abs(dy) > 1: # Ledge jump
            directions.append('Down')
        elif dx == 1: directions.append('Right')
        elif dx == -1: directions.append('Left')
        elif dy == 1: directions.append('Down')
        elif dy == -1: directions.append('Up')
    return directions

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    target_x = int(input_data['target_x'])
    target_y = int(input_data['target_y'])

    astar = AStar(map_xml_string)
    path_coords = astar.find_path((start_x, start_y), (target_x, target_y))

    # The final output should be a JSON object containing the path plan
    path_plan_output = {
        "path_found": 'error' not in path_coords[0] if path_coords else False,
        "path_plan": path_coords if ('error' not in path_coords[0] if path_coords else False) else None,
        "path_directions": convert_path_to_directions(path_coords) if ('error' not in path_coords[0] if path_coords else False) else None,
        "error_info": path_coords[0] if path_coords and 'error' in path_coords[0] else None
    }
    print(json.dumps(path_plan_output, indent=2))

except Exception as e:
    print(json.dumps({"error": str(e), "path_found": False, "path_plan": None, "path_directions": None}))
