import xml.etree.ElementTree as ET
from collections import deque
import json
import traceback

def analyze_segment_fixed(map_xml_string, start_x, start_y):
    try:
        root = ET.fromstring(map_xml_string)
        width = int(root.get('width'))
        height = int(root.get('height'))
        start_x, start_y = int(start_x), int(start_y)

        grid = {}
        for r in root.findall('Row'):
            y = int(r.get('id'))
            for t in r.findall('Tile'):
                x = int(t.get('id'))
                has_impassable_object = False
                obj_node = t.find('Object')
                if obj_node is not None and obj_node.get('id-name') != 'Pikachu':
                    has_impassable_object = True

                grid[(x, y)] = {
                    'type': t.get('type'),
                    'has_object': has_impassable_object,
                    'is_warp': t.get('is-warp') == 'true',
                    'original_object_node': obj_node
                }

        impassable_types = ['impassable', 'unknown', 'cuttable', 'water', 'closed_gate']

        queue = deque([(start_x, start_y)])
        visited = set([(start_x, start_y)])
        reachable_tiles_coords = set([(start_x, start_y)])
        reachable_warps, reachable_objects = [], []

        while queue:
            cx, cy = queue.popleft()

            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nx, ny = cx + dx, cy + dy

                if (nx, ny) not in visited:
                    neighbor_data = grid.get((nx, ny))
                    if neighbor_data:
                        visited.add((nx, ny))
                        if neighbor_data['type'] not in impassable_types and not neighbor_data['has_object']:
                            reachable_tiles_coords.add((nx, ny))
                            queue.append((nx, ny))

        for x, y in reachable_tiles_coords:
            tile_data = grid.get((x, y))
            if tile_data:
                if tile_data['is_warp']:
                    if {'x': x, 'y': y} not in reachable_warps:
                        reachable_warps.append({'x': x, 'y': y})

                obj_node = tile_data['original_object_node']
                if obj_node is not None:
                    obj_data = {
                        'id': obj_node.get('id'),
                        'id-name': obj_node.get('id-name'),
                        'name': obj_node.get('name'),
                        'x': x,
                        'y': y
                    }
                    if obj_data not in reachable_objects:
                        reachable_objects.append(obj_data)

        result = {
            "reachable_tiles_count": len(reachable_tiles_coords),
            "reachable_warps": reachable_warps,
            "reachable_objects": reachable_objects,
            "segment_size": len(reachable_tiles_coords)
        }
        return json.dumps(result, indent=2)

    except Exception as e:
        return json.dumps({"error": str(e), "traceback": traceback.format_exc()})

print(analyze_segment_fixed(map_xml_string, input_data['start_x'], input_data['start_y']))

