import xml.etree.ElementTree as ET
import heapq
import json

def find_path(map_xml_string, start_x, start_y, target_x, target_y, party_data_str):
    try:
        party_data = json.loads(party_data_str)
    except json.JSONDecodeError:
        print("Error: Invalid JSON in party_data.")
        return None

    root = ET.fromstring(map_xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))
    grid = [[None for _ in range(width + 1)] for _ in range(height + 1)]

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            grid[y][x] = tile_elem

    def has_hm(hm_name):
        for pokemon in party_data:
            if not pokemon.get('fainted', False):
                if 'moves' in pokemon and any(move.get('name') == hm_name for move in pokemon.get('moves', [])):
                    return True
        return False

    def is_walkable(x, y, from_x, from_y):
        if not (1 <= x <= width and 1 <= y <= height):
            return False
        tile = grid[y][x]
        if tile is None:
            return False
        tile_type = tile.get('type')

        from_tile = None
        if 1 <= from_x <= width and 1 <= from_y <= height:
            from_tile = grid[from_y][from_x]
        from_tile_type = from_tile.get('type') if from_tile is not None else None

        if tile.find('Object') is not None and tile.find('Object').get('id-name') != 'PIKACHU':
            return False
        if tile_type in ['impassable', 'boulder_barrier', 'closed_gate', 'unknown']:
            return False
        if tile_type == 'ledge':
            return y > from_y

        if tile_type == 'water':
            if not has_hm('SURF'):
                return False
            if from_tile_type != 'water':
                return False

        if tile_type == 'cuttable' and not has_hm('CUT'):
            return False
        return True

    def get_neighbors(x, y):
        neighbors = []
        for dx, dy, move in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            nx, ny = x + dx, y + dy
            if is_walkable(nx, ny, x, y):
                cost = 1
                tile = grid[ny][nx]
                if tile.get('type') == 'ledge':
                    if is_walkable(nx, ny + 1, nx, ny):
                        neighbors.append(((nx, ny + 1), cost, move))
                    continue
                neighbors.append(((nx, ny), cost, move))
        return neighbors

    open_set = [(0, (start_x, start_y), [])]
    closed_set = set()
    g_score = {(x, y): float('inf') for x in range(1, width + 1) for y in range(1, height + 1)}
    g_score[(start_x, start_y)] = 0

    final_target = (target_x, target_y)
    if not is_walkable(target_x, target_y, -1, -1):
        found_adjacent_target = False
        for dx, dy in [(0, -1), (0, 1), (-1, 0), (1, 0)]:
            adj_x, adj_y = target_x + dx, target_y + dy
            if is_walkable(adj_x, adj_y, -1, -1):
                final_target = (adj_x, adj_y)
                found_adjacent_target = True
                break
        if not found_adjacent_target:
            return None

    while open_set:
        _, current, path = heapq.heappop(open_set)
        if current == final_target:
            return path + [current]
        if current in closed_set:
            continue
        closed_set.add(current)
        for neighbor, cost, move in get_neighbors(current[0], current[1]):
            tentative_g_score = g_score[current] + cost
            if tentative_g_score < g_score.get(neighbor, float('inf')):
                g_score[neighbor] = tentative_g_score
                h_score = abs(neighbor[0] - final_target[0]) + abs(neighbor[1] - final_target[1])
                f_score = tentative_g_score + h_score
                new_path = path + [current]
                heapq.heappush(open_set, (f_score, neighbor, new_path))
    return None

player_pos_elem = ET.fromstring(map_xml_string).find('.//Player/..')
start_x, start_y = int(player_pos_elem.get('id')), int(player_pos_elem.get('coordinate').split(',')[1].strip(')'))
target_x, target_y = int(input_data['target_x']), int(input_data['target_y'])
party_data_str = input_data['party_data']

path = find_path(map_xml_string, start_x, start_y, target_x, target_y, party_data_str)
if path:
    print(json.dumps(path))
else:
    print("No path found.")

