import xml.etree.ElementTree as ET
import json
from collections import deque

def get_player_start_pos(root):
    for row in root.findall('Row'):
        for tile in row.findall('Tile'):
            if tile.find('Player') is not None:
                coord = tile.get('coordinate')
                x_str, y_str = coord.strip('()').split(', ')
                return int(x_str), int(y_str)
    return None, None

def is_traversable(tile, from_x, from_y, to_x, to_y):
    tile_type = tile.get('type')
    if tile.find('Object') is not None:
        return False
    traversable_types = ['FLOOR', 'TALL_GRASS', 'LONG_GRASS', 'DOOR', 'WARP_CARPET_DOWN', 'LADDER']
    one_way_down = ['LEDGE_HOP_DOWN']
    one_way_left = ['LEDGE_HOP_LEFT']
    one_way_right = ['LEDGE_HOP_RIGHT']

    if tile_type in traversable_types:
        return True
    if tile_type in one_way_down and from_y < to_y:
        return True
    if tile_type in one_way_left and from_x > to_x:
        return True
    if tile_type in one_way_right and from_x < to_x:
        return True
    return False


try:
    root = ET.fromstring(map_xml_string)
    player_x, player_y = get_player_start_pos(root)

    if player_x is None:
        print(json.dumps({"error": "Player position not found."}))
    else:
        target_coords_str = input_data.get('coordinates', '[]')
        target_coords = json.loads(target_coords_str)

        grid = {}
        for row_elem in root.findall('Row'):
            for tile_elem in row_elem.findall('Tile'):
                coord_str = tile_elem.get('coordinate')
                x, y = map(int, coord_str.strip('()').split(', '))
                grid[(x, y)] = tile_elem

        reachable_tiles = []
        for target in target_coords:
            target_x, target_y = target['x'], target['y']

            queue = deque([(player_x, player_y)])
            visited = set([(player_x, player_y)])
            found = False

            while queue:
                current_x, current_y = queue.popleft()

                if (current_x, current_y) == (target_x, target_y):
                    reachable_tiles.append(target)
                    found = True
                    break

                for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                    next_x, next_y = current_x + dx, current_y + dy

                    if (next_x, next_y) in grid and (next_x, next_y) not in visited:
                        tile = grid[(next_x, next_y)]
                        if is_traversable(tile, current_x, current_y, next_x, next_y):
                            visited.add((next_x, next_y))
                            queue.append((next_x, next_y))

        print(json.dumps({"reachable_tiles": reachable_tiles}))

except Exception as e:
    print(json.dumps({"error": str(e)}))

