import heapq
import xml.etree.ElementTree as ET

def find_path_from_xml(map_xml_string, start_x, start_y, end_x, end_y):
    root = ET.fromstring(map_xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))

    grid = [['' for _ in range(width + 1)] for _ in range(height + 1)]
    occupied_tiles = set()

    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            grid[y][x] = tile.get('type')
            if tile.find('Object') is not None:
                occupied_tiles.add((x, y))

    traversable = {'FLOOR', 'TALL_GRASS', 'LONG_GRASS', 'DOOR', 'WARP_CARPET_DOWN', 'LADDER', 'CAVE'}
    one_way_down = {'LEDGE_HOP_DOWN'}
    one_way_right = {'LEDGE_HOP_RIGHT'}
    one_way_left = {'LEDGE_HOP_LEFT'}

    start = (int(start_x), int(start_y))
    end = (int(end_x), int(end_y))

    def heuristic(a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    open_list = []
    heapq.heappush(open_list, (0, start))
    came_from = {}
    g_score = {start: 0}
    f_score = {start: heuristic(start, end)}

    while open_list:
        _, current = heapq.heappop(open_list)

        if current == end:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            return path[::-1]

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)
            nx, ny = neighbor

            if not (1 <= nx <= width and 1 <= ny <= height):
                continue

            if neighbor in occupied_tiles:
                continue

            tile_type = grid[ny][nx]

            is_valid_move = False
            if tile_type in traversable:
                is_valid_move = True
            elif tile_type in one_way_down and dy == 1:
                is_valid_move = True
            elif tile_type in one_way_right and dx == 1:
                is_valid_move = True
            elif tile_type in one_way_left and dx == -1:
                is_valid_move = True

            if not is_valid_move:
                continue

            tentative_g_score = g_score.get(current, float('inf')) + 1

            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = tentative_g_score + heuristic(neighbor, end)
                heapq.heappush(open_list, (f_score[neighbor], neighbor))

    return None

path = find_path_from_xml(map_xml_string, input_data['start_x'], input_data['start_y'], input_data['end_x'], input_data['end_y'])

if path:
    path_str = ' -> '.join(map(str, path))
    print(f"Path found: {path_str}")
else:
    print("No path found.")
