import json
import collections
import xml.etree.ElementTree as ET

def find_path_bfs(map_xml_string, start_x, start_y, end_x, end_y, is_surfing_str='false', temp_impassable_coords_str=None):
    is_surfing = is_surfing_str.lower() == 'true' if is_surfing_str is not None else False
    root = ET.fromstring(map_xml_string)
    map_width = int(root.get('width'))
    map_height = int(root.get('height'))
    tiles = {}
    for row in root.findall('Row'):
        row_id = int(row.get('id'))
        for tile_elem in row.findall('Tile'):
            tile_id = int(tile_elem.get('id'))
            x, y = tile_id, row_id
            tile_type = tile_elem.get('type')
            has_object = tile_elem.get('has-object') == 'true'
            object_id = None
            for obj_elem in tile_elem.findall('Object'):
                object_id = obj_elem.get('id')
                break
            tiles[(x, y)] = {'type': tile_type, 'has_object': has_object, 'object_id': object_id}

    start = (int(start_x), int(start_y))
    end_goal = (int(end_x), int(end_y))

    traversable_types = {'ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'ladder_up', 'ladder_down'}
    if is_surfing:
        traversable_types.add('water')

    def is_traversable(x, y):
        if not (1 <= x <= map_width and 1 <= y <= map_height): return False
        tile_info = tiles.get((x, y))
        if not tile_info or tile_info['type'] == 'unknown': return False
        if tile_info['type'] in ['impassable', 'boulder_barrier', 'closed_gate', 'cuttable', 'ledge']: return False
        if not is_surfing and tile_info['type'] == 'water': return False
        if tile_info['has_object'] and tile_info['object_id'] != '15': return False
        return tile_info['type'] in traversable_types

    goal_set = set()
    if is_traversable(end_goal[0], end_goal[1]):
        goal_set.add(end_goal)
    else:
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (end_goal[0] + dx, end_goal[1] + dy)
            if is_traversable(neighbor[0], neighbor[1]):
                goal_set.add(neighbor)

    if not goal_set:
        print(json.dumps({'path_found': False, 'error': 'Destination and all adjacent tiles are not traversable.'}))
        return

    queue = collections.deque([(start, [start])])
    visited = {start}

    while queue:
        (current_x, current_y), path = queue.popleft()
        current_pos = (current_x, current_y)

        if current_pos in goal_set:
            print(json.dumps({'path_found': True, 'path': path}))
            return

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor_pos = (current_x + dx, current_y + dy)

            if neighbor_pos not in visited and is_traversable(neighbor_pos[0], neighbor_pos[1]):
                current_tile_type = tiles.get(current_pos)['type']
                neighbor_tile_type = tiles.get(neighbor_pos)['type']

                if current_tile_type == 'water' and neighbor_tile_type != 'water':
                    if neighbor_tile_type not in {'ground', 'grass'}:
                        continue

                visited.add(neighbor_pos)
                new_path = list(path)
                new_path.append(neighbor_pos)
                queue.append((neighbor_pos, new_path))

    print(json.dumps({'path_found': False, 'error': 'No path found.'}))

