import json
import re

def navigate_party_menu(target_pokemon, screen_text):
    lines = screen_text.strip().split('\n')
    pokemon_names = []
    for line in lines:
        match = re.match(r'([A-Z\[\]]+)\s+LV\s*\d+', line.strip())
        if match:
            pokemon_names.append(match.group(1).strip())
        else:
            # Handle the format with the arrow
            match_arrow = re.match(r'â\s*([A-Z\[\]]+)', line.strip())
            if match_arrow:
                pokemon_names.append(match_arrow.group(1).strip())

    if not pokemon_names:
        # Fallback for different text formats
        for i, line in enumerate(lines):
            # Clean up lines to extract names, this is a bit brittle but covers observed formats
            cleaned_line = re.sub(r'LV\s*\d+|â|\s+\d+/\d+', '', line).strip()
            if cleaned_line and i % 2 == 0 and 'Choose' not in cleaned_line:
                pokemon_names.append(cleaned_line)

    try:
        current_pos = -1
        for i, line in enumerate(lines):
            if 'â' in line:
                # Determine which pokemon is selected based on line index
                current_pos = i // 2 # Each pokemon takes up 2 lines
                break

        if current_pos == -1:
            print(json.dumps({'error': 'Could not determine current cursor position.'}))
            return

        target_pos = pokemon_names.index(target_pokemon.upper())

        moves = target_pos - current_pos

        buttons = []
        if moves > 0:
            buttons.extend(['Down'] * moves)
        elif moves < 0:
            buttons.extend(['Up'] * abs(moves))

        print(json.dumps(buttons))

    except (ValueError, IndexError) as e:
        print(json.dumps({'error': f'Could not find target pokemon or parse menu. Target: {target_pokemon}, Found: {pokemon_names}, Error: {str(e)}'}))

navigate_party_menu(input_data['target_pokemon'], input_data['screen_text'])

