import json
import re

def navigate_fly_menu(screen_text, target_city):
    city_order = [
        "PALLET TOWN", "VIRIDIAN CITY", "PEWTER CITY", "CERULEAN CITY",
        "LAVENDER TOWN", "VERMILION CITY", "CELADON CITY", "FUCHSIA CITY",
        "CINNABAR ISLAND", "SAFFRON CITY", "INDIGO PLATEAU"
    ]

    match = re.search(r"To ([\w\s]+)", screen_text)
    if not match:
        return json.dumps({"error": "Could not parse current city from screen text."})

    current_city = match.group(1).strip().upper()
    # Normalize whitespace in target_city to prevent errors from extra spaces
    target_city_normalized = re.sub(r'\s+', ' ', target_city).strip().upper()

    if current_city not in city_order:
        return json.dumps({"error": f"Invalid current city parsed: '{current_city}'. Valid options are: {city_order}"})
    if target_city_normalized not in city_order:
        return json.dumps({"error": f"Invalid target city: '{target_city}'. Valid options are: {city_order}"})

    current_index = city_order.index(current_city)
    target_index = city_order.index(target_city_normalized)

    diff = target_index - current_index
    if diff == 0:
        return json.dumps([])

    # The menu wraps around, so find the shortest path
    path_down = diff if diff >= 0 else diff + len(city_order)
    path_up = abs(diff) if diff < 0 else len(city_order) - diff

    if path_down <= path_up:
        moves = ["Down"] * path_down
    else:
        moves = ["Up"] * path_up

    return json.dumps(moves)

print(navigate_fly_menu(input_data['screen_text'], input_data['target_city']))
