import xml.etree.ElementTree as ET
import json
from collections import deque

def find_path_with_warnings():
    try:
        root = ET.fromstring(map_xml_string)
        target_x = int(input_data['target_x'])
        target_y = int(input_data['target_y'])
    except (ET.ParseError, ValueError, KeyError) as e:
        print(json.dumps({'error': f'Input or XML parsing error: {e}'}))
        return

    player_pos = None
    grid, all_obstacles, npcs = {}, set(), {}
    all_x, all_y = [], []
    static_object_names = {'FRUIT_TREE', 'ITEM_BALL', 'GYM_GUIDE', 'TEACHER', 'FISHER', 'BUG_CATCHER', 'WEIRD_TREE', 'POKE_BALL', 'ROCKET', 'BEAUTY', 'JIGGLYPUFF_SPRITE', 'BLACK_BELT', 'GENTLEMAN', 'PHARMACIST', 'SUPER_NERD', 'RECEPTIONIST'}

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        all_y.append(y)
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            all_x.append(x)
            grid[(x, y)] = tile_elem
            if tile_elem.find('Player') is not None:
                player_pos = (x, y)
            if tile_elem.get('has-object') == 'true':
                all_obstacles.add((x, y))
                for obj in tile_elem.findall('Object'):
                    obj_id = obj.get('id')
                    obj_id_name = obj.get('id-name')
                    if obj_id is not None and obj_id_name not in static_object_names:
                        try:
                            npc_id = int(obj_id)
                            if npc_id > 0: npcs[npc_id] = (x, y)
                        except (ValueError, TypeError): continue
            for marker in tile_elem.findall('Marker'):
                if marker.get('object_id') is not None and marker.get('emoji') != "☠️":
                    all_obstacles.add((x, y))

    if (18, 28) in grid:
        tile_18_28 = grid.get((18, 28))
        if tile_18_28 is not None:
            print(f"DIAGNOSTIC: Tile (18,28) type is {tile_18_28.get('type')}")
        else:
            print("DIAGNOSTIC: Tile (18,28) is None in grid")
    else:
        print("DIAGNOSTIC: Tile (18,28) not found in grid")

    if not all_x or not all_y or not player_pos:
        print(json.dumps({'error': 'Map or player data incomplete.'}))
        return

    min_x, max_x, min_y, max_y = min(all_x), max(all_x), min(all_y), max(all_y)

    impassable_tile_types = {'WALL', 'VOID', 'PC', 'TV', 'COUNTER', 'BOOKSHELF', 'WINDOW', 'TOWN_MAP', 'POKEDEX', 'RADIO', 'PILLAR', 'MART_SHELF', 'INCENSE_BURNER', 'CUT_TREE', 'HEADBUTT_TREE', 'BUOY', 'unseen', 'WHIRLPOOL'}
    impassable_warps = {'DOOR', 'STAIRCASE', 'PIT', 'CAVE'}

    queue = deque([(player_pos, [])])
    visited = {player_pos}

    target_tile = grid.get((target_x, target_y))
    target_is_impassable = (target_tile is None or target_tile.get('type') in impassable_tile_types or (target_x, target_y) in all_obstacles)

    final_path = None
    while queue:
        (x, y), path = queue.popleft()

        if not target_is_impassable and (x, y) == (target_x, target_y):
            final_path = path + [{'x': x, 'y': y}]; break

        if target_is_impassable:
            for dx_adj, dy_adj in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                if (x + dx_adj, y + dy_adj) == (target_x, target_y):
                    final_path = path + [{'x': x, 'y': y}]; break
            if final_path: break

        current_tile = grid.get((x,y))
        current_tile_type = current_tile.get('type') if current_tile else None

        moves = [(0, 1), (0, -1), (1, 0), (-1, 0)]

        for dx, dy in moves:
            neighbor = (x + dx, y + dy)
            if not (min_x <= neighbor[0] <= max_x and min_y <= neighbor[1] <= max_y) or neighbor in visited: continue

            tile = grid.get(neighbor)
            if tile is None: continue

            neighbor_tile_type = tile.get('type')

            if neighbor_tile_type in impassable_tile_types or neighbor in all_obstacles: continue
            if neighbor_tile_type in impassable_warps and neighbor != (target_x, target_y): continue

            if current_tile_type == 'FLOOR_UP_WALL' and (dx, dy) == (0, -1): continue
            if neighbor_tile_type == 'FLOOR_UP_WALL' and (dx, dy) == (0, -1): continue

            if current_tile_type == 'LEDGE_HOP_DOWN' and (dx, dy) != (0, 1): continue
            if current_tile_type == 'LEDGE_HOP_LEFT' and (dx, dy) != (-1, 0): continue
            if current_tile_type == 'LEDGE_HOP_RIGHT' and (dx, dy) != (1, 0): continue

            if neighbor_tile_type == 'LEDGE_HOP_DOWN' and (dx, dy) == (0, -1): continue
            if neighbor_tile_type == 'LEDGE_HOP_LEFT' and (dx, dy) == (1, 0): continue
            if neighbor_tile_type == 'LEDGE_HOP_RIGHT' and (dx, dy) == (-1, 0): continue

            visited.add(neighbor)
            queue.append((neighbor, path + [{'x': x, 'y': y}]))

    if final_path:
        print(json.dumps({'path': final_path, 'warnings': []}))
    else:
        print(json.dumps({'error': 'No path found.'}))

find_path_with_warnings()

