import xml.etree.ElementTree as ET
import json
from collections import deque

def find_nearest_unseen():
    try:
        root = ET.fromstring(map_xml_string)
    except ET.ParseError as e:
        print(json.dumps({'error': f'XML parsing error: {e}'}))
        return

    player_pos = None
    grid = {}
    seen_tiles = set()
    unseen_tiles = set()

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            grid[(x, y)] = tile_elem
            if tile_elem.get('seen') == 'true':
                seen_tiles.add((x, y))
            else:
                unseen_tiles.add((x, y))
            if tile_elem.find('Player') is not None:
                player_pos = (x, y)

    if not player_pos:
        print(json.dumps({'error': 'Player position not found.'}))
        return

    impassable_types = {'WALL', 'VOID', 'WATER', 'unseen', 'PC', 'TV', 'COUNTER', 'BOOKSHELF', 'WINDOW', 'TOWN_MAP', 'POKEDEX', 'RADIO', 'PILLAR', 'MART_SHELF', 'INCENSE_BURNER', 'DOOR', 'STAIRCASE', 'PIT', 'CAVE'}

    queue = deque([player_pos])
    visited = {player_pos}

    while queue:
        x, y = queue.popleft()

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            adj_x, adj_y = x + dx, y + dy
            neighbor_coords = (adj_x, adj_y)

            if neighbor_coords in unseen_tiles:
                print(json.dumps({'target_x': x, 'target_y': y}))
                return

            if neighbor_coords not in visited and neighbor_coords in seen_tiles:
                tile = grid.get(neighbor_coords)
                if tile is not None:
                    tile_type = tile.get('type')
                    if tile_type not in impassable_types:
                        visited.add(neighbor_coords)
                        queue.append(neighbor_coords)

    print(json.dumps({'error': 'No reachable unseen tiles found.'}))

find_nearest_unseen()

