import json
import re

def normalize_text(text):
    return re.sub(r'[^a-zA-Z0-9éÉ\s]', '', text.strip())

def navigate():
    try:
        try:
            menu_options_list = json.loads(input_data['menu_options'])
        except json.JSONDecodeError:
            menu_options_list = input_data['menu_options']
        current_option = input_data['current_option']
        target_option = input_data['target_option']
    except KeyError as e:
        print(json.dumps({'error': f'Missing input parameter: {e}'}))
        return

    normalized_options = [normalize_text(opt) for opt in menu_options_list]
    normalized_current = normalize_text(current_option)
    normalized_target = normalize_text(target_option)

    try:
        current_index = normalized_options.index(normalized_current)
        target_index = normalized_options.index(normalized_target)
    except ValueError:
        print(json.dumps({'error': 'Current or target option not found in menu options.'}))
        return

    presses = []
    diff = target_index - current_index

    if diff > 0:
        for _ in range(diff):
            presses.append('Right')
    elif diff < 0:
        for _ in range(abs(diff)):
            presses.append('Left')

    presses.append('A')
    print(json.dumps(presses))

navigate()
