import xml.etree.ElementTree as ET
from collections import deque
import json

def find_path(map_xml, start, target, mode='walking'):
    try:
        root = ET.fromstring(map_xml)
    except Exception as e:
        return []

    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    grid = {}

    # Parse map
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            type_ = tile.attrib['type']
            has_object = tile.attrib.get('has-object', 'false') == 'true'
            grid[(x, y)] = {'type': type_, 'has_object': has_object}

    queue = deque([(start, [])])
    visited = set([start])

    while queue:
        (cx, cy), path = queue.popleft()

        if (cx, cy) == target:
            return path

        # Directions: Up, Down, Left, Right
        moves = [
            (0, -1, 'Up'), (0, 1, 'Down'), 
            (-1, 0, 'Left'), (1, 0, 'Right')
        ]

        for dx, dy, btn in moves:
            nx, ny = cx + dx, cy + dy
            if (nx, ny) in grid and (nx, ny) not in visited:
                tile = grid[(nx, ny)]

                # Basic collision logic
                # Treat objects as walls unless it is the target (e.g. interacting with NPC/Door)
                if tile['has_object'] and (nx, ny) != target:
                    continue 

                t_type = tile['type']
                walkable = False

                if mode == 'walking':
                    if t_type == 'FLOOR' or t_type == 'LADDER' or t_type == 'CUT_TREE' or t_type == 'WARP_CARPET_DOWN' or t_type == 'WARP_CARPET_UP' or t_type == 'WARP_CARPET_LEFT' or t_type == 'WARP_CARPET_RIGHT':
                        walkable = True
                    # Treat others as obstacles
                elif mode == 'surfing':
                    if t_type == 'WATER':
                        walkable = True

                if walkable:
                    visited.add((nx, ny))
                    queue.append(((nx, ny), path + [btn]))

    return None

