import xml.etree.ElementTree as ET
import collections
import json

def get_path():
    try:
        # map_xml_string is injected by the environment
        root = ET.fromstring(map_xml_string)
    except Exception as e:
        print(json.dumps([]))
        return

    grid = {}
    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            t_type = tile.get('type')

            # Check for blocking objects
            has_object = False
            for child in tile:
                if child.tag == 'Object':
                    has_object = True
                    break

            grid[(x,y)] = {'type': t_type, 'has_object': has_object}

    start = (start_x, start_y)
    target = (target_x, target_y)

    if start not in grid or target not in grid:
        print(json.dumps([]))
        return

    queue = collections.deque([[start]])
    seen = {start}

    while queue:
        path = queue.popleft()
        curr = path[-1]

        if curr == target:
            btns = []
            for i in range(len(path)-1):
                cx, cy = path[i]
                nx, ny = path[i+1]
                if nx > cx: btns.append('Right')
                elif nx < cx: btns.append('Left')
                elif ny > cy: btns.append('Down')
                elif ny < cy: btns.append('Up')
            print(json.dumps(btns))
            return

        cx, cy = curr
        neighbors = [
            (cx, cy-1, 'Up'),
            (cx, cy+1, 'Down'),
            (cx-1, cy, 'Left'),
            (cx+1, cy, 'Right')
        ]

        for nx, ny, move in neighbors:
            if (nx, ny) in seen: continue
            if (nx, ny) not in grid: continue

            tile = grid[(nx, ny)]

            if tile['has_object'] and (nx, ny) != start and (nx, ny) != target:
                continue

            t = tile['type']
            walkable = True
            if t == 'WALL': walkable = False
            elif t == 'CUT_TREE': walkable = False
            elif 'LEDGE_HOP_DOWN' in t and move != 'Down': walkable = False
            elif 'LEDGE_HOP_UP' in t and move != 'Up': walkable = False
            elif 'LEDGE_HOP_LEFT' in t and move != 'Left': walkable = False
            elif 'LEDGE_HOP_RIGHT' in t and move != 'Right': walkable = False

            if walkable:
                seen.add((nx, ny))
                new_path = list(path)
                new_path.append((nx, ny))
                queue.append(new_path)

    print(json.dumps([]))

get_path()

