import json
import xml.etree.ElementTree as ET
from collections import deque

def main(input_data):
    target_x = int(input_data['target_x'])
    target_y = int(input_data['target_y'])
    root = ET.fromstring(map_xml_string)
    traversable_types = {"FLOOR", "STAIRCASE", "WARP_CARPET", "WARP_CARPET_DOWN", "WARP_CARPET_UP", "WARP_CARPET_LEFT", "WARP_CARPET_RIGHT", "LADDER", "GRASS", "CAVE", "SAND", "SNOW", "TALL_GRASS", "unseen"}
    grid = {}
    player_pos = None
    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            t_type = tile.get('type')
            if tile.find('Player') is not None:
                player_pos = (x, y)
            is_passable = t_type in traversable_types and tile.find('Object') is None
            grid[(x, y)] = {"passable": is_passable, "type": t_type}
    if player_pos is None:
        print(json.dumps([]))
        return
    queue = deque([(player_pos, [])])
    visited = {player_pos}
    while queue:
        (curr_x, curr_y), path = queue.popleft()
        if curr_x == target_x and curr_y == target_y:
            print(json.dumps(path))
            return
        if abs(curr_x - target_x) + abs(curr_y - target_y) == 1:
            if not grid.get((target_x, target_y), {}).get("passable"):
                print(json.dumps(path))
                return
        for dx, dy, move in [(0, -1, "Up"), (0, 1, "Down"), (-1, 0, "Left"), (1, 0, "Right")]:
            nx, ny = curr_x + dx, curr_y + dy
            if (nx, ny) in grid and grid[(nx, ny)]["passable"] and (nx, ny) not in visited:
                visited.add((nx, ny))
                queue.append(((nx, ny), path + [move]))
    print(json.dumps([]))

if __name__ == "__main__":
    main(input_data)

check_reachable_unseen_v1.python_script:
import json
import xml.etree.ElementTree as ET
from collections import deque

def main(input_data):
    root = ET.fromstring(map_xml_string)
    walkable_types = ["FLOOR", "STAIRCASE", "WARP_CARPET", "WARP_CARPET_DOWN", "WARP_CARPET_UP", "WARP_CARPET_LEFT", "WARP_CARPET_RIGHT", "LADDER", "GRASS", "CAVE", "SAND", "SNOW", "TALL_GRASS"]

    autopress = input_data.get('autopress_buttons', False)

    grid = {}
    player_pos = None
    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            t_type = tile.get('type')
            if tile.find('Player') is not None:
                player_pos = (x, y)
            is_passable = t_type in walkable_types and tile.find('Object') is None
            grid[(x, y)] = {"passable": is_passable, "seen": tile.get('seen') == 'true'}

    if player_pos is None:
        print(json.dumps([]))
        return

    if autopress:
        queue = deque([(player_pos, [])])
        visited = {player_pos}
        while queue:
            (curr_x, curr_y), path = queue.popleft()
            for dx, dy, move in [(0, -1, "Up"), (0, 1, "Down"), (-1, 0, "Left"), (1, 0, "Right")]:
                nx, ny = curr_x + dx, curr_y + dy
                if (nx, ny) in grid:
                    if not grid[(nx, ny)]["seen"]:
                        print(json.dumps(path))
                        return
                    if grid[(nx, ny)]["passable"] and (nx, ny) not in visited:
                        visited.add((nx, ny))
                        queue.append(((nx, ny), path + [move]))
        print(json.dumps([]))
    else:
        reachable_unseen = []
        for (x, y), tile_info in grid.items():
            if not tile_info["seen"]:
                for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                    neighbor = grid.get((x + dx, y + dy))
                    if neighbor is not None and neighbor["seen"] and neighbor["passable"]:
                        reachable_unseen.append({"x": x, "y": y})
                        break
        print(json.dumps(reachable_unseen))

if __name__ == "__main__":
    main(input_data)

find_object_by_name_v1.python_script:
import json
import xml.etree.ElementTree as ET

def main(input_data):
    # map_xml_string is automatically provided by the harness
    root = ET.fromstring(map_xml_string)
    target_name = input_data['object_name'].upper()
    results = []

    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))

            # Check Map Objects (NPCs, items)
            for obj in tile.findall('Object'):
                obj_name = obj.get('id-name', '').upper()
                if target_name in obj_name:
                    results.append({"name": obj.get('id-name'), "x": x, "y": y, "type": "MapObject"})

            # Check Background Objects (Signs, PCs, Vending Machines)
            bg_obj_name = tile.get('name', '').upper()
            if target_name in bg_obj_name:
                results.append({"name": tile.get('name'), "x": x, "y": y, "type": "BackgroundObject"})

    print(json.dumps(results))

if __name__ == "__main__":
    main(input_data)
