import xml.et.ree.ElementTree as ET
import heapq

root = ET.fromstring(map_xml_string)
map_width = int(root.get('width'))
map_height = int(root.get('height'))
player_pos = None

grid = [[0 for _ in range(map_width + 1)] for _ in range(map_height + 1)]
tiles = {}

for row_elem in root.findall('Row'):
    y = int(row_elem.get('id'))
    for tile_elem in row_elem.findall('Tile'):
        x = int(tile_elem.get('id'))
        tile_type = tile_elem.get('type')
        tiles[(x, y)] = tile_type
        if tile_elem.find('Player') is not None:
            player_pos = (x, y)
        impassable_types = ['WALL', 'WINDOW', 'CUT_TREE', 'SIGN', 'BOOKSHELF', 'BLACKBOARD', 'MART_SHELF', 'BUOY', 'TV', 'TOWN_MAP', 'BIRD', 'HEADBUTT_TREE', 'FRUIT_TREE', 'COMPUTER', 'PRINTER', 'VOID', 'WATER', 'CAVE', 'COUNTER', 'PC', 'CUT_08', 'CUT_28_GARBAGE', 'RADIO', 'INCENSE_BURNER']
        if tile_type in impassable_types or tile_elem.find('Object') is not None:
            grid[y][x] = 1

if not player_pos:
    print("Error: Player position not found.")
    exit()

start = player_pos
target_x = int(input_data['target_x'])
target_y = int(input_data['target_y'])
goal = (target_x, target_y)

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def get_neighbors(pos):
    x, y = pos
    neighbors = []
    for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
        nx, ny = x + dx, y + dy
        if 1 <= nx <= map_width and 1 <= ny <= map_height:
            neighbor_tile_type = tiles.get((nx, ny))
            if grid[ny][nx] == 1:
                continue
            if neighbor_tile_type == 'LEDGE_HOP_DOWN' and dy != 1:
                continue
            if neighbor_tile_type == 'LEDGE_HOP_LEFT' and dx != -1:
                continue
            if neighbor_tile_type == 'LEDGE_HOP_RIGHT' and dx != 1:
                continue
            if neighbor_tile_type == 'FLOOR_UP_WALL' and dy != -1:
                continue
            neighbors.append(((nx, ny), 1))
    return neighbors

def a_star_search(start, goal):
    frontier = []
    heapq.heappush(frontier, (0, start))
    came_from = {start: None}
    cost_so_far = {start: 0}
    while not len(frontier) == 0:
        current_priority, current_node = heapq.heappop(frontier)
        if current_node == goal:
            break
        for next_node, cost in get_neighbors(current_node):
            new_cost = cost_so_far[current_node] + cost
            if next_node not in cost_so_far or new_cost < cost_so_far[next_node]:
                cost_so_far[next_node] = new_cost
                priority = new_cost + heuristic(goal, next_node)
                heapq.heappush(frontier, (priority, next_node))
                came_from[next_node] = current_node
    path = []
    current = goal
    while current != start:
        if current not in came_from:
            return None
        path.append(current)
        current = came_from[current]
    path.append(start)
    path.reverse()
    if path[0] == start:
        return path
    else:
        return None

path = a_star_search(start, goal)
if path:
    path_str = "->".join([f"({x},{y})" for x, y in path])
    print(f"Path found: {path_str}")
else:
    print("No path found.")

