import xml.etree.ElementTree as ET
from collections import deque
import json

# Expected globals: map_xml_string, start_x, start_y, target_x, target_y, mode

def bfs_path(start, target, mode='walking'):
    try:
        root = ET.fromstring(map_xml_string)
    except:
        return []

    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    grid = {}
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib.get('type', 'FLOOR')

            # Check for objects
            has_object = False
            for child in tile:
                if child.tag == 'Object':
                    has_object = True
                    break
            # Also check attribute if present
            if tile.attrib.get('has_object') == 'true':
                has_object = True

            grid[(x, y)] = {'type': t_type, 'has_object': has_object}

    queue = deque([(start, [])])
    visited = {start}

    directions = [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]

    while queue:
        (curr_x, curr_y), path = queue.popleft()

        if (curr_x, curr_y) == target:
            return path

        for dx, dy, move_name in directions:
            nx, ny = curr_x + dx, curr_y + dy

            # Basic Bounds
            if not (0 <= nx < width and 0 <= ny < height):
                continue

            target_tile = grid.get((nx, ny))
            if not target_tile:
                continue

            t_type = target_tile['type']
            has_obj = target_tile['has_object']

            # Determine landing spot (handling ledges)
            final_nx, final_ny = nx, ny
            is_ledge_hop = False

            # Ledge Logic
            if 'LEDGE_HOP_DOWN' in t_type:
                if move_name == 'Down':
                    final_ny += 1
                    is_ledge_hop = True
                else: continue
            elif 'LEDGE_HOP_UP' in t_type:
                if move_name == 'Up':
                    final_ny -= 1
                    is_ledge_hop = True
                else: continue
            elif 'LEDGE_HOP_LEFT' in t_type:
                if move_name == 'Left':
                    final_nx -= 1
                    is_ledge_hop = True
                else: continue
            elif 'LEDGE_HOP_RIGHT' in t_type:
                if move_name == 'Right':
                    final_nx += 1
                    is_ledge_hop = True
                else: continue

            # Check landing spot bounds
            if not (0 <= final_nx < width and 0 <= final_ny < height):
                continue

            landing_tile = grid.get((final_nx, final_ny))
            if not landing_tile: continue

            l_type = landing_tile['type']
            l_obj = landing_tile['has_object']

            # Obstacle Checks (on Landing Tile)
            # Objects are obstacles unless target
            if l_obj and (final_nx, final_ny) != target:
                continue

            # Mode-based terrain checks
            if mode == 'walking':
                if 'WATER' in l_type or 'WHIRLPOOL' in l_type:
                    continue
                # Block Walls
                if 'WALL' in l_type or 'TREE' in l_type or 'BOULDER' in l_type or 'SIGN' in l_type or 'PC' in l_type:
                    continue
            elif mode == 'surfing':
                # Can traverse WATER
                if 'WATER' in l_type or 'WHIRLPOOL' in l_type:
                    pass
                elif 'WALL' in l_type: # Walls still block
                    continue
                else: # Landing on land while surfing? Allowed (dismount)
                    pass

            if (final_nx, final_ny) not in visited:
                visited.add((final_nx, final_ny))
                queue.append(((final_nx, final_ny), path + [move_name]))

    return []

# Run
p = bfs_path((start_x, start_y), (target_x, target_y), mode)
print(json.dumps(p))
