import xml.etree.ElementTree as ET
import heapq
import json

def find_path_logic(map_xml_string, start_x, start_y, end_x, end_y):
    root = ET.fromstring(map_xml_string)
    IMPASSABLE_TILES = {"WALL", "WATER", "HEADBUTT_TREE", "CUT_TREE", "COUNTER", "TV", "BOOKSHELF", "RADIO", "TOWN_MAP", "WINDOW"}
    grid = {}
    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')
            has_object = tile_elem.find('Object') is not None and tile_elem.find('Player') is None
            grid[(x, y)] = {'type': tile_type, 'has_object': has_object}

    start_node = (start_x, start_y)
    end_node = (end_x, end_y)

    if end_node in grid and (grid[end_node]['type'] in IMPASSABLE_TILES or grid[end_node]['has_object']):
        found_alternate = False
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            temp_end = (end_node[0] + dx, end_node[1] + dy)
            if temp_end in grid and grid[temp_end]['type'] not in IMPASSABLE_TILES and not grid[temp_end]['has_object']:
                end_node = temp_end
                found_alternate = True
                break
        if not found_alternate:
            return None

    open_set = []
    heapq.heappush(open_set, (0, start_node))
    came_from = {}
    g_score = {start_node: 0}
    f_score = {start_node: abs(start_node[0] - end_node[0]) + abs(start_node[1] - end_node[1])}

    while open_set:
        _, current = heapq.heappop(open_set)
        if current == end_node:
            return True

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)
            if neighbor not in grid:
                continue

            current_tile_info = grid.get(current)
            neighbor_tile_info = grid.get(neighbor)

            if not neighbor_tile_info or neighbor_tile_info['has_object'] or neighbor_tile_info['type'] in IMPASSABLE_TILES:
                continue

            current_type = current_tile_info['type']
            neighbor_type = neighbor_tile_info['type']

            is_valid_move = True
            if neighbor_type == "LEDGE_HOP_DOWN" and dy != 1: is_valid_move = False
            elif neighbor_type == "LEDGE_HOP_RIGHT" and dx != 1: is_valid_move = False
            elif neighbor_type == "LEDGE_HOP_LEFT" and dx != -1: is_valid_move = False

            if current_type == "LEDGE_HOP_DOWN" and dy == -1: is_valid_move = False
            elif current_type == "LEDGE_HOP_RIGHT" and dx == -1: is_valid_move = False
            elif current_type == "LEDGE_HOP_LEFT" and dx == 1: is_valid_move = False

            if not is_valid_move:
                continue

            tentative_g_score = g_score.get(current, float('inf')) + 1
            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = tentative_g_score + abs(neighbor[0] - end_node[0]) + abs(neighbor[1] - end_node[1])
                if neighbor not in [i[1] for i in open_set]:
                    heapq.heappush(open_set, (f_score[neighbor], neighbor))
    return None

