import xml.etree.ElementTree as ET
import json

try:
    target_x = int(input_data['target_x'])
    target_y = int(input_data['target_y'])

    root = ET.fromstring(map_xml_string)

    impassable_types = {
        'WALL', 'VOID', 'WATER', 'BOOKSHELF', 'COUNTER', 'CUT_TREE',
        'HEADBUTT_TREE', 'MART_SHELF', 'PC', 'PILLAR', 'RADIO', 'TOWN_MAP',
        'TV', 'WINDOW'
    }

    grid = {}
    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')
            has_object = tile_elem.find('Object') is not None

            is_impassable = (tile_type in impassable_types) or has_object
            grid[(x, y)] = not is_impassable

    adjacent_traversable = []
    for dx, dy, direction in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
        nx, ny = target_x + dx, target_y + dy
        neighbor_coord = (nx, ny)

        if grid.get(neighbor_coord, False):
            adjacent_traversable.append({'x': nx, 'y': ny, 'direction_from_target': direction})

    print(json.dumps(adjacent_traversable))

except Exception as e:
    print(json.dumps({"error": str(e)}))
