import json

path_json = input_data['path_json']
player_x = int(input_data['player_x'])
player_y = int(input_data['player_y'])
destination_x = int(input_data['destination_x'])
destination_y = int(input_data['destination_y'])

path = json.loads(path_json)

if not path or (path[0][0] != player_x or path[0][1] != player_y):
    print(json.dumps({'status': 'REPLAN', 'message': 'Player is not at the start of the path. Re-planning is needed.'}))
else:
    if len(path) > 1:
        next_x, next_y = path[1]
        if next_x > player_x:
            move = 'Right'
        elif next_x < player_x:
            move = 'Left'
        elif next_y > player_y:
            move = 'Down'
        elif next_y < player_y:
            move = 'Up'
        else:
            move = None
        print(json.dumps({'status': 'MOVE', 'move': move}))
    elif player_x == destination_x and player_y == destination_y:
        print(json.dumps({'status': 'ARRIVED', 'message': 'Destination reached.'}))
    else:
        print(json.dumps({'status': 'STUCK', 'message': 'Cannot determine next move.'}))

