import heapq
import xml.etree.ElementTree as ET
import json

def find_path(start_x, start_y, end_x, end_y, traversable_tiles, obstacles_json=None):
    root = ET.fromstring(map_xml_string)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])
    grid = [[None for _ in range(width)] for _ in range(height)]

    all_obstacles = set()

    if obstacles_json and obstacles_json.strip():
        try:
            dynamic_obstacles = json.loads(obstacles_json)
            for obs in dynamic_obstacles:
                all_obstacles.add((obs['x'], obs['y']))
        except (json.JSONDecodeError, KeyError):
            pass

    for row_elem in root.findall('Row'):
        y = int(row_elem.attrib['id'])
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.attrib['id'])
            tile_type = tile_elem.attrib.get('type')
            is_warp = tile_elem.attrib.get('is-warp') == 'true'
            has_object = tile_elem.attrib.get('has-object') == 'true'
            grid[y][x] = {'type': tile_type, 'is_warp': is_warp, 'has_object': has_object, 'elem': tile_elem}

            if tile_type not in traversable_tiles and not is_warp:
                all_obstacles.add((x, y))
            elif has_object:
                all_obstacles.add((x, y))

            for marker in tile_elem.findall('Marker'):
                if marker.attrib.get('emoji') == '☠️':
                    all_obstacles.add((x, y))

    def heuristic(a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    start_node = (start_x, start_y)
    end_node = (end_x, end_y)

    if start_node in all_obstacles:
        all_obstacles.remove(start_node)
    if end_node in all_obstacles:
        all_obstacles.remove(end_node)

    g_score = { (x,y): float('inf') for x in range(width) for y in range(height) }
    f_score = { (x,y): float('inf') for x in range(width) for y in range(height) }

    g_score[start_node] = 0
    f_score[start_node] = heuristic(start_node, end_node)

    open_set = [(f_score[start_node], start_node)]
    came_from = {}

    while open_set:
        _, current = heapq.heappop(open_set)

        if current == end_node:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            path.append(start_node)
            return json.dumps(path[::-1])

        x, y = current

        if not (0 <= y < height and 0 <= x < width) or grid[y][x] is None:
            continue

        current_tile_elem = grid[y][x]['elem']
        current_tile_type = current_tile_elem.attrib.get('type')

        possible_moves = [(0, 1), (0, -1), (1, 0), (-1, 0)]
        if current_tile_type and 'LEDGE_HOP_DOWN' in current_tile_type:
            possible_moves = [(0, 1)] # Can only move down
        elif current_tile_type and 'LEDGE_HOP_RIGHT' in current_tile_type:
            possible_moves = [(1, 0)] # Can only move right
        elif current_tile_type and 'LEDGE_HOP_LEFT' in current_tile_type:
            possible_moves = [(-1, 0)] # Can only move left

        for dx, dy in possible_moves:
            neighbor = (x + dx, y + dy)
            nx, ny = neighbor

            if not (0 <= nx < width and 0 <= ny < height):
                continue

            if grid[ny][nx] is None:
                continue

            neighbor_tile_elem = grid[ny][nx]['elem']
            neighbor_tile_type = neighbor_tile_elem.attrib.get('type')

            # Prevent moving INTO ledges from the wrong direction
            if dy == -1 and neighbor_tile_type and 'LEDGE_HOP_DOWN' in neighbor_tile_type: continue
            if dx == -1 and neighbor_tile_type and 'LEDGE_HOP_RIGHT' in neighbor_tile_type: continue
            if dx == 1 and neighbor_tile_type and 'LEDGE_HOP_LEFT' in neighbor_tile_type: continue

            if neighbor in all_obstacles:
                continue

            tentative_g_score = g_score[current] + 1

            if tentative_g_score < g_score[neighbor]:
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = g_score[neighbor] + heuristic(neighbor, end_node)
                heapq.heappush(open_set, (f_score[neighbor], neighbor))

    return json.dumps(None)

start_x = int(input_data['start_x'])
start_y = int(input_data['start_y'])
end_x = int(input_data['end_x'])
end_y = int(input_data['end_y'])
traversable_tiles = input_data['traversable_tiles']
obstacles_json = input_data.get('obstacles_json')

path = find_path(start_x, start_y, end_x, end_y, traversable_tiles, obstacles_json)
print(path)
