import json
import collections
import xml.etree.ElementTree as ET

def debug_log(message):
    # print(f'[DEBUG] {message}')
    pass

try:
    debug_log('Tool execution started.')
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])
    movement_state = input_data['movement_state']
    debug_log(f'Inputs: start=({start_x},{start_y}), end=({end_x},{end_y}), state={movement_state}')

    root = ET.fromstring(map_xml_string)
    map_width = int(root.get('width'))
    map_height = int(root.get('height'))
    tiles = {}
    debug_log('Parsing map XML...')
    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile_elem in row.findall('Tile'):
            x = int(tile_elem.get('id'))
            obj = tile_elem.find('Object')
            tiles[(x, y)] = {
                'type': tile_elem.get('type'),
                'has_object': tile_elem.get('has-object') == 'true',
                'object_id_name': obj.get('id-name') if obj is not None else None
            }
    debug_log(f'Map parsed. Dimensions: {map_width}x{map_height}. Total tiles parsed: {len(tiles)}')

    start = (start_x, start_y)
    end = (end_x, end_y)

    base_traversable_tile_types = {
        'ground', 'grass', 'elevated_ground', 'steps', 'ladder_up', 
        'ladder_down', 'teleport', 'hole', 'cleared_boulder_barrier',
        'open_gate', 'gate_offscreen'
    }

    warp_tile_types = {'ladder_up', 'ladder_down', 'teleport', 'hole'}

    if movement_state == 'surfing':
        base_traversable_tile_types.add('water')
    debug_log(f'Traversable tile types: {base_traversable_tile_types}')

    def is_valid_move(pos, from_pos=None):
        x, y = pos
        if not (1 <= x <= map_width and 1 <= y <= map_height):
            return False

        tile_info = tiles.get(pos)
        if not tile_info:
            return False

        tile_type = tile_info['type']

        if tile_type in warp_tile_types and pos != end:
            return False

        if tile_type not in base_traversable_tile_types:
            return False

        if tile_info['has_object'] and (tile_info.get('object_id_name') or '').upper() != 'PIKACHU':
            return False

        if from_pos:
            current_tile_info = tiles.get(from_pos)
            current_type = current_tile_info.get('type')
            next_type = tile_info.get('type')

            is_elevation_change = (current_type == 'ground' and next_type == 'elevated_ground') or \
                                  (current_type == 'elevated_ground' and next_type == 'ground')

            is_via_steps = (current_type == 'steps' or next_type == 'steps')

            if is_elevation_change and not is_via_steps:
                return False

        return True

    goal_tiles = []
    if is_valid_move(end):
        goal_tiles.append(end)
        debug_log(f'Destination ({end_x},{end_y}) is directly traversable.')
    else:
        debug_log(f'Destination ({end_x},{end_y}) is not directly traversable. Checking adjacent tiles.')
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            adj_pos = (end_x + dx, end_y + dy)
            if is_valid_move(adj_pos):
                goal_tiles.append(adj_pos)
    debug_log(f'Final goal tiles: {goal_tiles}')

    if not goal_tiles:
        reason = f'Destination ({end_x},{end_y}) and all adjacent tiles are impassable.'
        debug_log(f'Pathfinding failed: {reason}')
        print(json.dumps({'success': False, 'reason': reason, 'path_plan': []}))
        exit()

    queue = collections.deque([(start, [start])])
    visited = {start}
    path_found = False
    final_path_coords = []
    debug_log('Starting BFS...')

    bfs_count = 0
    while queue:
        bfs_count += 1
        current_pos, path = queue.popleft()
        if bfs_count % 100 == 0:
            debug_log(f'BFS step {bfs_count}, queue size: {len(queue)}, current: {current_pos}')

        if current_pos in goal_tiles:
            final_path_coords = path
            path_found = True
            debug_log(f'Path found! Length: {len(path)}. Target: {current_pos}')
            break

        (cx, cy) = current_pos
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            next_pos = (cx + dx, cy + dy)

            if next_pos in visited:
                continue

            if tiles.get(next_pos, {}).get('type') == 'ledge':
                if dy == 1 and dx == 0: # Moving down
                    final_pos = (cx, cy + 2)
                    if final_pos not in visited and is_valid_move(final_pos, from_pos=current_pos):
                        visited.add(final_pos)
                        new_path = list(path)
                        new_path.append(final_pos)
                        queue.append((final_pos, new_path))
                continue

            if is_valid_move(next_pos, from_pos=current_pos):
                visited.add(next_pos)
                new_path = list(path)
                new_path.append(next_pos)
                queue.append((next_pos, new_path))

    if path_found:
        final_path_plan = [{'x': p[0], 'y': p[1]} for p in final_path_coords]
        print(json.dumps({'success': True, 'reason': 'Path found.', 'path_plan': final_path_plan}))
    else:
        debug_log('BFS completed. No path found.')

        start_tile_info = tiles.get(start, {})
        end_tile_info = tiles.get(end, {})
        start_type = start_tile_info.get('type')
        end_type = end_tile_info.get('type')

        is_start_ground = start_type in {'ground', 'steps'}
        is_start_elevated = start_type in {'elevated_ground', 'steps'}
        is_end_ground = end_type in {'ground', 'steps'}
        is_end_elevated = end_type in {'elevated_ground', 'steps'}

        if (is_start_ground and is_end_elevated) or (is_start_elevated and is_end_ground):
            steps_in_visited = any(tiles.get(v, {}).get('type') == 'steps' for v in visited)
            if not steps_in_visited:
                reason = 'No path found. The destination is on a different elevation and no connecting steps were found in the reachable area.'
                print(json.dumps({'success': False, 'reason': reason, 'path_plan': []}))
                exit()

        frontier = set()
        for pos in visited:
            (cx, cy) = pos
            for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                neighbor_pos = (cx + dx, cy + dy)
                if neighbor_pos not in visited:
                    frontier.add(neighbor_pos)

        closest_obstacle = None
        min_dist = float('inf')

        for obstacle_pos in frontier:
            dist = abs(obstacle_pos[0] - start_x) + abs(obstacle_pos[1] - start_y)
            if dist < min_dist:
                tile_info = tiles.get(obstacle_pos)
                if tile_info:
                    obstacle_type = tile_info.get('type')
                    if obstacle_type not in base_traversable_tile_types or (tile_info['has_object'] and (tile_info.get('object_id_name') or '').upper() != 'PIKACHU'):
                        min_dist = dist
                        closest_obstacle = {
                            'pos': obstacle_pos,
                            'type': obstacle_type,
                            'object': tile_info.get('object_id_name')
                        }

        reason = 'No path found. '
        if closest_obstacle:
            obs_type = closest_obstacle['type']
            obs_pos = closest_obstacle['pos']
            obs_obj = closest_obstacle['object']
            if obs_obj and obs_obj.upper() != 'PIKACHU':
                 reason += f"Path blocked by object '{obs_obj}' at ({obs_pos[0]},{obs_pos[1]})."
            elif obs_type == 'boulder':
                 reason += f"Path blocked by a movable boulder at ({obs_pos[0]},{obs_pos[1]})."
            elif obs_type == 'boulder_barrier':
                 reason += f"Path blocked by a boulder barrier at ({obs_pos[0]},{obs_pos[1]})."
            else:
                 reason += f"Path blocked by impassable tile of type '{obs_type}' at ({obs_pos[0]},{obs_pos[1]})."
        else:
            warps_in_visited = any(tiles.get(v, {}).get('type') in warp_tile_types for v in visited)
            if not warps_in_visited:
                reason += 'The destination is in a separate, unreachable map partition. No connecting warps or ladders were found in the explored area between the start and end points.'
            else:
                reason += 'The destination is in a separate, unreachable map partition.'

        debug_log(f'Pathfinding failed: {reason}')
        print(json.dumps({'success': False, 'reason': reason, 'path_plan': []}))

except Exception as e:
    error_message = f'An error occurred: {str(e)}'
    debug_log(error_message)
    print(json.dumps({'success': False, 'reason': error_message, 'path_plan': []}))

