import json
import collections
import xml.etree.ElementTree as ET

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    target_tile_type = input_data['target_tile_type']

    root = ET.fromstring(map_xml_string)
    map_width = int(root.get('width'))
    map_height = int(root.get('height'))
    tiles = {}
    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile_elem in row.findall('Tile'):
            x = int(tile_elem.get('id'))
            obj = tile_elem.find('Object')
            marker = tile_elem.find('Marker')
            tiles[(x, y)] = {
                'type': tile_elem.get('type'),
                'has_object': tile_elem.get('has-object') == 'true',
                'object_id_name': obj.get('id-name') if obj is not None else None,
                'has_marker': marker is not None
            }

    interaction_points = set()
    for (x, y), tile_info in tiles.items():
        if tile_info['type'] == target_tile_type:
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                adj_pos = (x + dx, y + dy)
                adj_tile_info = tiles.get(adj_pos)
                if adj_tile_info and adj_tile_info['type'] in {'ground', 'grass', 'elevated_ground', 'steps'} and not adj_tile_info['has_marker']:
                    interaction_points.add(adj_pos)

    if not interaction_points:
        print(json.dumps({'success': False, 'reason': 'No unmarked, reachable interaction points found for the target tile type.', 'path_plan': []}))
        exit()

    start_pos = (start_x, start_y)
    queue = collections.deque([(start_pos, [start_pos])])
    visited = {start_pos}

    base_traversable_tile_types = {'ground', 'grass', 'elevated_ground', 'steps', 'ladder_up', 'ladder_down', 'teleport', 'hole'}

    path_found = None

    while queue:
        current_pos, path = queue.popleft()

        if current_pos in interaction_points:
            path_found = path
            break

        (cx, cy) = current_pos
        current_tile_info = tiles.get(current_pos)
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            next_pos = (cx + dx, cy + dy)
            if next_pos in visited:
                continue

            if not (1 <= next_pos[0] <= map_width and 1 <= next_pos[1] <= map_height):
                continue

            next_tile_info = tiles.get(next_pos)
            if not next_tile_info or next_tile_info['type'] not in base_traversable_tile_types:
                continue

            if current_tile_info and {current_tile_info['type'], next_tile_info['type']} == {'ground', 'elevated_ground'}:
                continue

            if next_tile_info['has_object'] and next_tile_info.get('object_id_name', '').upper() != 'PIKACHU':
                continue

            visited.add(next_pos)
            new_path = list(path)
            new_path.append(next_pos)
            queue.append((next_pos, new_path))

    if path_found:
        final_path_coords = [{'x': p[0], 'y': p[1]} for p in path_found]
        final_path_coords.append(final_path_coords[-1])
        print(json.dumps({'success': True, 'reason': 'Path to nearest interaction point found.', 'path_plan': final_path_coords}))
    else:
        print(json.dumps({'success': False, 'reason': 'Could not find a path to any of the interaction points.', 'path_plan': []}))

except Exception as e:
    print(json.dumps({'success': False, 'reason': f'An error occurred: {str(e)}', 'path_plan': []}))
