import xml.etree.ElementTree as ET
import heapq
import json

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def find_path_on_graph(start_node, end_node, graph, debug=False):
    open_set = []
    heapq.heappush(open_set, (0, start_node))
    came_from = {}
    g_score = {node: float('inf') for node in graph}
    g_score[start_node] = 0
    f_score = {node: float('inf') for node in graph}
    f_score[start_node] = heuristic(start_node, end_node)

    if debug:
        print(f"Starting pathfinding from {start_node} to {end_node}")

    while open_set:
        _, current = heapq.heappop(open_set)

        if debug:
            print(f"Visiting node: {current}")

        if current == end_node:
            path = []
            temp = current
            while temp in came_from:
                path.append(temp)
                temp = came_from[temp]
            path.append(start_node)
            path.reverse()
            return path

        for neighbor, weight in graph.get(current, {}).items():
            tentative_g_score = g_score[current] + weight
            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = tentative_g_score + heuristic(neighbor, end_node)
                if neighbor not in [i[1] for i in open_set]:
                    heapq.heappush(open_set, (f_score[neighbor], neighbor))
    return None

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])
    base_traversable_tiles = {tile.strip() for tile in input_data.get('traversable_tiles', '').split(',')}
    hms_available = {hm.strip().upper() for hm in input_data.get('hms_available', '').split(',') if hm.strip()}
    debug = input_data.get('debug', 'false').lower() == 'true'

    root = ET.fromstring(map_xml_string)
    tile_data = {}
    nodes = set()
    for row_elem in root.findall('Row'):
        y = int(row_elem.attrib['id'])
        for tile_elem in row_elem.findall('Tile'):
            if tile_elem.attrib.get('seen') != 'true':
                continue
            x = int(tile_elem.attrib['id'])
            tile_type = tile_elem.attrib['type']
            has_object = tile_elem.attrib.get('has-object') == 'true'
            tile_data[(x, y)] = {'type': tile_type, 'has_object': has_object}
            is_traversable = tile_type in base_traversable_tiles
            if not has_object and is_traversable:
                nodes.add((x, y))

    graph = {}
    for node in nodes:
        graph[node] = {}
        node_type = tile_data[node]['type']
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (node[0] + dx, node[1] + dy)
            if neighbor not in nodes: continue
            neighbor_type = tile_data.get(neighbor, {}).get('type')
            if not neighbor_type: continue
            if node_type == 'LEDGE_HOP_DOWN' and (dx != 0 or dy != 1): continue
            if node_type == 'LEDGE_HOP_LEFT' and (dx != -1 or dy != 0): continue
            if node_type == 'LEDGE_HOP_RIGHT' and (dx != 1 or dy != 0): continue
            is_node_fuw = node_type == 'FLOOR_UP_WALL'
            is_neighbor_fuw = neighbor_type == 'FLOOR_UP_WALL'
            if is_neighbor_fuw and not is_node_fuw and dy != -1: continue
            if is_node_fuw and not is_neighbor_fuw and dy != 1: continue
            graph[node][neighbor] = 1

    start_node = (start_x, start_y)
    target_node = (end_x, end_y)

    if start_node not in graph:
        print(json.dumps({'error': f'Start node ({start_node}) is not in the traversable graph.'}))
        exit()

    if target_node not in graph:
        if debug:
            print(f"Target node {target_node} is not traversable. Finding closest adjacent node.")
        possible_targets = []
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            adj_node = (end_x + dx, end_y + dy)
            if adj_node in graph:
                possible_targets.append(adj_node)

        if not possible_targets:
            print(json.dumps({'error': 'No traversable adjacent tiles found for the target.'}))
            exit()

        closest_target = min(possible_targets, key=lambda p: heuristic(start_node, p))
        if debug:
            print(f"New target node: {closest_target}")
        target_node = closest_target

    final_path = find_path_on_graph(start_node, target_node, graph, debug)
    if final_path:
        print(json.dumps({'path': [{'x': p[0], 'y': p[1]} for p in final_path]}))
    else:
        print(json.dumps({'error': 'No path found.'}))
except Exception as e:
    print(json.dumps({'error': f'An error occurred: {e}'}))

