import xml.etree.ElementTree as ET
import json

def find_unmarked_tiles(xml_string, x_min, x_max, y_min, y_max, emoji):
    root = ET.fromstring(xml_string)
    unmarked = []
    for row in root.findall('Row'):
        y = int(row.get('id'))
        if y_min <= y <= y_max:
            for tile in row.findall('Tile'):
                x = int(tile.get('id'))
                if x_min <= x <= x_max:
                    if tile.get('type') == 'FLOOR':
                        has_marker = any(marker.get('emoji') == emoji for marker in tile.findall('Marker'))
                        if not has_marker:
                            unmarked.append({"x": x, "y": y})
    return unmarked

print(json.dumps(find_unmarked_tiles(map_xml_string, int(input_data['x_min']), int(input_data['x_max']), int(input_data['y_min']), int(input_data['y_max']), input_data['emoji'])))
