import xml.etree.ElementTree as ET

def check_area(xml_string, s_x, s_y, e_x, e_y, t_type=None, t_obj=None):
    root = ET.fromstring(xml_string)
    results = []
    for row in root.findall("Row"):
        y_val = int(row.get("id"))
        if s_y <= y_val <= e_y:
            for tile in row.findall("Tile"):
                x_val = int(tile.get("id"))
                if s_x <= x_val <= e_x:
                    found = False
                    if t_type and tile.get("type") == t_type:
                        found = True
                    obj = tile.find("Object")
                    if t_obj and obj is not None and (t_obj in (obj.get("id-name") or "") or t_obj in (obj.get("name") or "")):
                        found = True
                    if not t_type and not t_obj:
                        found = True

                    if found:
                        results.append({
                            "coord": (x_val, y_val),
                            "type": tile.get("type"),
                            "object": obj.get("id-name") if obj is not None else None,
                            "bg_object": obj.get("name") if obj is not None else None
                        })
    return results

res = check_area(map_xml_string, int(input_data["start_x"]), int(input_data["start_y"]), int(input_data["end_x"]), int(input_data["end_y"]), input_data.get("target_type"), input_data.get("target_object_name"))
print(res)
