import heapq
import xml.etree.ElementTree as ET
import json

def find_path(start_x, start_y, end_x, end_y, traversable_tiles, xml_string):
    root = ET.fromstring(xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))

    grid = [['' for _ in range(width + 1)] for _ in range(height + 1)]
    objects = set()

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            grid[y][x] = tile_elem.get('type')
            if tile_elem.find('Object') is not None:
                objects.add((y, x))

    start = (start_y, start_x)

    is_end_traversable = grid[end_y][end_x] in traversable_tiles and (end_y, end_x) not in objects

    potential_goals = []
    if is_end_traversable:
        potential_goals.append((end_y, end_x))
    else:
        for dy, dx in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            ny, nx = end_y + dy, end_x + dx
            if 1 <= ny <= height and 1 <= nx <= width and grid[ny][nx] in traversable_tiles and (ny, nx) not in objects:
                potential_goals.append((ny, nx))

    if not potential_goals:
        return json.dumps({"error": "No path found to a traversable tile near the destination."})

    shortest_path = None
    for goal in potential_goals:
        path = a_star(grid, objects, start, goal, width, height, traversable_tiles)
        if path:
            if shortest_path is None or len(path) < len(shortest_path):
                shortest_path = path

    if shortest_path:
        return json.dumps([{"x": x, "y": y} for y, x in shortest_path])
    else:
        return json.dumps({"error": "No path found."})

def a_star(grid, objects, start, goal, width, height, traversable_tiles):
    open_set = []
    heapq.heappush(open_set, (0, start))
    came_from = {}
    g_score = { (r, c): float('inf') for r in range(height + 2) for c in range(width + 2) }
    g_score[start] = 0
    f_score = { (r, c): float('inf') for r in range(height + 2) for c in range(width + 2) }
    f_score[start] = heuristic(start, goal)

    while open_set:
        _, current = heapq.heappop(open_set)

        if current == goal:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            path.append(start)
            return path[::-1]

        for dy, dx in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dy, current[1] + dx)

            if not (1 <= neighbor[0] <= height and 1 <= neighbor[1] <= width):
                continue

            current_tile_type = grid[current[0]][current[1]]
            neighbor_tile_type = grid[neighbor[0]][neighbor[1]]

            if neighbor_tile_type not in traversable_tiles or neighbor in objects:
                continue

            if 'LEDGE_HOP_DOWN' in neighbor_tile_type and dy == -1:
                continue
            if 'LEDGE_HOP_LEFT' in neighbor_tile_type and dx == 1:
                continue
            if 'LEDGE_HOP_RIGHT' in neighbor_tile_type and dx == -1:
                continue

            tentative_g_score = g_score[current] + 1
            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = g_score[neighbor] + heuristic(neighbor, goal)
                if neighbor not in [item[1] for item in open_set]:
                    heapq.heappush(open_set, (f_score[neighbor], neighbor))
    return None

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

