        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = curr_x + dx, curr_y + dy

            if 0 <= nx < width and 0 <= ny < height:
                if (nx, ny) not in visited:
                    visited.add((nx, ny))
                    tile = tiles.get((nx, ny))
                    if tile:
                        if not tile['seen']:
                            return (nx, ny)
                        if tile['type'] == 'FLOOR' and not tile['obj']:
                            queue.append((nx, ny))
