import json
import xml.etree.ElementTree as ET
from collections import deque

def list_reachable_unseen():
    tree = ET.fromstring(map_xml_string)
    width = int(tree.attrib['width'])
    height = int(tree.attrib['height'])

    player_pos = None
    tiles = {}
    for row in tree.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            seen = tile.attrib['seen'] == 'true'
            t_type = tile.attrib['type']
            has_player = tile.find('Player') is not None
            has_object = tile.find('Object') is not None

            tiles[(x, y)] = {'seen': seen, 'type': t_type, 'obj': has_object}
            if has_player:
                player_pos = (x, y)

    if not player_pos:
        return []

    unseen_reachable = []
    queue = deque([player_pos])
    visited = {player_pos}

    while queue:
        curr_x, curr_y = queue.popleft()

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = curr_x + dx, curr_y + dy

            if 0 <= nx < width and 0 <= ny < height:
                if (nx, ny) not in visited:
                    tile = tiles.get((nx, ny))
                    if tile:
                        if not tile['seen']:
                            unseen_reachable.append((nx, ny))
                            visited.add((nx, ny))
                        elif tile['type'] == 'FLOOR' and not tile['obj']:
                            visited.add((nx, ny))
                            queue.append((nx, ny))

    if not unseen_reachable:
        return []

    # Simple BFS to find buttons to the first unseen_reachable
    target = unseen_reachable[0]
    queue = deque([(player_pos, [])])
    visited = {player_pos}
    while queue:
        (cx, cy), path = queue.popleft()
        if (cx, cy) == target:
            return path

        for dx, dy, btn in [(0, 1, "Down"), (0, -1, "Up"), (1, 0, "Right"), (-1, 0, "Left")]:
            nx, ny = cx + dx, cy + dy
            if (nx, ny) == target:
                return path + [btn]
            if 0 <= nx < width and 0 <= ny < height:
                tile = tiles.get((nx, ny))
                if tile and tile['seen'] and tile['type'] == 'FLOOR' and not tile['obj'] and (nx, ny) not in visited:
                    visited.add((nx, ny))
                    queue.append(((nx, ny), path + [btn]))
    return []

print(json.dumps(list_reachable_unseen()))
