import xml.etree.ElementTree as ET
from collections import deque

def bfs_path(start, target, grid, width, height):
    queue = deque([(start, [])])
    visited = {start}

    while queue:
        (curr_x, curr_y), path = queue.popleft()

        if (curr_x, curr_y) == target:
            return path + [{"x": curr_x, "y": curr_y}]

        # Directions: Up, Down, Left, Right
        moves = [
            (0, -1, "Up"), (0, 1, "Down"), 
            (-1, 0, "Left"), (1, 0, "Right")
        ]

        for dx, dy, direction in moves:
            nx, ny = curr_x + dx, curr_y + dy

            if 0 <= nx < width and 0 <= ny < height:
                if (nx, ny) not in visited:
                    tile_type = grid.get((nx, ny), "unseen")
                    # Basic collision logic
                    if tile_type != "WALL" and tile_type != "unseen":
                        visited.add((nx, ny))
                        new_path = path + [{"x": curr_x, "y": curr_y}]
                        queue.append(((nx, ny), new_path))

    return None

def coords_to_buttons(path):
    buttons = []
    if not path or len(path) < 2:
        return buttons

    for i in range(len(path) - 1):
        curr = path[i]
        next_tile = path[i+1]

        dx = next_tile['x'] - curr['x']
        dy = next_tile['y'] - curr['y']

        if dx == 1: buttons.append("Right")
        elif dx == -1: buttons.append("Left")
        elif dy == 1: buttons.append("Down")
        elif dy == -1: buttons.append("Up")

    return buttons

